const EmptyState = styled.div`
  text-align: center;
  padding: 80px 20px;
  color: ${props => props.theme.textSecondary};
`;

const EmptyIcon = styled.div`
  font-size: 4rem;
  margin-bottom: 24px;
`;

const EmptyTitle = styled.h3`
  font-size: 1.5rem;
  font-weight: 600;
  margin-bottom: 12px;
  color: ${props => props.theme.text};
`;

const EmptyDescription = styled.p`
  font-size: 1rem;
  max-width: 400px;
  margin: 0 auto;
`;

const Explore = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [category, setCategory] = useState('');
  const [sortBy, setSortBy] = useState('newest');
  const [viewMode, setViewMode] = useState('grid');
  const [page, setPage] = useState(1);

  // Fetch categories
  const { data: categories } = useQuery(
    'categories',
    async () => {
      const response = await axios.get('/api/categories');
      return response.data.categories;
    }
  );

  // Fetch works
  const { data: worksData, isLoading, isFetching } = useQuery(
    ['works', searchQuery, category, sortBy, page],
    async () => {
      const params = new URLSearchParams({
        page: page.toString(),
        limit: '20',
        sort: sortBy,
      });

      if (searchQuery) params.append('search', searchQuery);
      if (category) params.append('category', category);

      const response = await axios.get(`/api/works?${params}`);
      return response.data;
    }
  );

  const handleSearch = (e) => {
    e.preventDefault();
    setPage(1);
  };

  const handleLoadMore = () => {
    setPage(prev => prev + 1);
  };

  const works = worksData?.works || [];
  const hasMore = worksData?.pagination?.current < worksData?.pagination?.pages;

  return (
    <Container>
      <Content>
        <Header>
          <Title>Ke≈üfet</Title>
          <Subtitle>
            Yaratƒ±cƒ± eserleri ke≈üfedin, ilham alƒ±n ve yeni tasarƒ±mcƒ±larla tanƒ±≈üƒ±n
          </Subtitle>
        </Header>

        <FiltersContainer>
          <SearchContainer>
            <form onSubmit={handleSearch}>
              <SearchInput
                type="text"
                placeholder="Eserler, tasarƒ±mcƒ±lar ara..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
              />
              <SearchIcon />
            </form>
          </SearchContainer>

          <FilterSelect
            value={category}
            onChange={(e) => {
              setCategory(e.target.value);
              setPage(1);
            }}
          >
            <option value="">T√ºm Kategoriler</option>
            {categories?.map(cat => (
              <option key={cat._id} value={cat._id}>
                {cat.name}
              </option>
            ))}
          </FilterSelect>

          <SortSelect
            value={sortBy}
            onChange={(e) => {
              setSortBy(e.target.value);
              setPage(1);
            }}
          >
            <option value="newest">En Yeni</option>
            <option value="oldest">En Eski</option>
            <option value="mostLiked">En √áok Beƒüenilen</option>
            <option value="mostViewed">En √áok G√∂r√ºnt√ºlenen</option>
            <option value="featured">√ñne √áƒ±kanlar</option>
          </SortSelect>

          <ViewToggle>
            <ViewButton
              active={viewMode === 'grid'}
              onClick={() => setViewMode('grid')}
            >
              <FiGrid size={18} />
              Grid
            </ViewButton>
            <ViewButton
              active={viewMode === 'list'}
              onClick={() => setViewMode('list')}
            >
              <FiList size={18} />
              Liste
            </ViewButton>
          </ViewToggle>
        </FiltersContainer>

        {isLoading ? (
          <LoadingSpinner text="Eserler y√ºkleniyor..." />
        ) : works.length > 0 ? (
          <>
            <ResultsHeader>
              <ResultsCount>
                {worksData?.pagination?.total || 0} eser bulundu
              </ResultsCount>
            </ResultsHeader>

            {viewMode === 'grid' ? (
              <WorksGrid>
                {works.map((work, index) => (
                  <motion.div
                    key={work._id}
                    initial={{ opacity: 0, y: 30 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ duration: 0.6, delay: index * 0.05 }}
                  >
                    <WorkCard work={work} />
                  </motion.div>
                ))}
              </WorksGrid>
            ) : (
              <WorksList>
                {works.map((work, index) => (
                  <motion.div
                    key={work._id}
                    initial={{ opacity: 0, x: -30 }}
                    animate={{ opacity: 1, x: 0 }}
                    transition={{ duration: 0.6, delay: index * 0.05 }}
                  >
                    <WorkCard work={work} />
                  </motion.div>
                ))}
              </WorksList>
            )}

            {hasMore && (
              <LoadMoreButton
                onClick={handleLoadMore}
                disabled={isFetching}
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
              >
                {isFetching ? 'Y√ºkleniyor...' : 'Daha Fazla Y√ºkle'}
              </LoadMoreButton>
            )}
          </>
        ) : (
          <EmptyState>
            <EmptyIcon>üîç</EmptyIcon>
            <EmptyTitle>Eser bulunamadƒ±</EmptyTitle>
            <EmptyDescription>
              Arama kriterlerinize uygun eser bulunamadƒ±. 
              Farklƒ± anahtar kelimeler deneyebilir veya filtreleri deƒüi≈ütirebilirsiniz.
            </EmptyDescription>
          </EmptyState>
        )}
      </Content>
    </Container>
  );
};

export default Explore;
