const Navbar = () => {
  const { user, logout } = useAuth();
  const { isDark, toggleTheme } = useTheme();
  const navigate = useNavigate();
  const location = useLocation();
  const [showUserMenu, setShowUserMenu] = useState(false);
  const [showMobileMenu, setShowMobileMenu] = useState(false);
  const [showUserDropdown, setShowUserDropdown] = useState(false);
  const [isMusicPlaying, setIsMusicPlaying] = useState(false);
  const [currentTrack, setCurrentTrack] = useState(0);
  const [audio, setAudio] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');

  // Sanatsal müzik dosyaları - Ücretsiz jazz müzik kaynakları
  const musicTracks = [
    {
      name: "Jazz Müzik",
      url: "/music/jazz1.mp3", // Yerel jazz müzik dosyası
      artist: "Feellink Jazz Collection"
    }
  ];

  const toggleMusic = () => {
    if (isMusicPlaying) {
      // Müziği durdur
      if (audio) {
        audio.pause();
        audio.currentTime = 0;
      }
      setIsMusicPlaying(false);
    } else {
      // Müziği başlat
      playMusic();
    }
  };

  const nextTrack = () => {
    // Tek müzik olduğu için aynı müziği tekrar başlat
    if (isMusicPlaying) {
      playMusic();
    }
  };

  // Gerçek müzik çalma fonksiyonu
  const playMusic = () => {
    // Önceki müziği durdur
    if (audio) {
      audio.pause();
      audio.currentTime = 0;
    }

    // Yeni müzik oluştur
    const newAudio = new Audio(musicTracks[currentTrack].url);

    // Müzik yüklendiğinde çal
    newAudio.addEventListener('canplaythrough', () => {
      newAudio.play().catch(error => {
        console.log('Müzik çalma hatası:', error);
        // Eğer müzik çalınamazsa, simülasyon göster
        console.log(`🎵 Şu anda çalıyor: ${musicTracks[currentTrack].name}`);
      });
    });

    // Müzik bittiğinde tekrar başlat
    newAudio.addEventListener('ended', () => {
      if (isMusicPlaying) {
        playMusic(); // Aynı müziği tekrar başlat
      }
    });

    // Hata durumunda simülasyon göster
    newAudio.addEventListener('error', () => {
      console.log(`🎵 Şu anda çalıyor: ${musicTracks[currentTrack].name}`);
    });

    setAudio(newAudio);
    setIsMusicPlaying(true);
  };

  const handleLogout = () => {
    logout();
    setShowUserMenu(false);
    // navigate('/') removed - AuthContext handles redirect to /login
  };

  const handleSearch = () => {
    if (searchQuery.trim()) {
      navigate(`/explore?search=${encodeURIComponent(searchQuery)}`);
    }
  };



  const isActive = (path) => {
    return location.pathname === path;
  };

  return (
    <Nav>
      <NavContainer>
        <NavActions>
          {user && (
            <UserMenu>
              <UserIcon onClick={() => setShowUserDropdown(!showUserDropdown)}>
                <FiUser size={20} />
              </UserIcon>

              <AnimatePresence>
                {showUserDropdown && (
                  <UserDropdown
                    initial={{ opacity: 0, y: -10 }}
                    animate={{ opacity: 1, y: 0 }}
                    exit={{ opacity: 0, y: -10 }}
                    transition={{ duration: 0.2 }}
                  >
                    <DropdownItem onClick={() => navigate('/profile')}>
                      <FiUser size={16} />
                      Profil
                    </DropdownItem>
                    <DropdownItem onClick={() => navigate('/saved')}>
                      <FiBookmark size={16} />
                      Kaydedilenler
                    </DropdownItem>
                    <DropdownItem onClick={() => navigate('/notifications')}>
                      <FiBell size={16} />
                      Bildirimler
                    </DropdownItem>
                    <DropdownDivider />
                    <DropdownItem onClick={handleLogout}>
                      <FiLogOut size={16} />
                      Çıkış Yap
                    </DropdownItem>
                  </UserDropdown>
                )}
              </AnimatePresence>
            </UserMenu>
          )}

          <MusicToggle onClick={toggleMusic} isPlaying={isMusicPlaying}>
            {isMusicPlaying ? <FiVolume2 size={20} /> : <FiVolumeX size={20} />}
          </MusicToggle>

          <ThemeToggle onClick={toggleTheme}>
            {isDark ? <FiSun size={20} /> : <FiMoon size={20} />}
          </ThemeToggle>
        </NavActions>
      </NavContainer>

      <AnimatePresence>
        {showMobileMenu && (
          <MobileMenu
            initial={{ opacity: 0, height: 0 }}
            animate={{ opacity: 1, height: 'auto' }}
            exit={{ opacity: 0, height: 0 }}
            transition={{ duration: 0.3 }}
          >
            {!user && (
              <>
                <MobileNavLink to="/login" onClick={() => setShowMobileMenu(false)}>
                  Giriş Yap
                </MobileNavLink>
                <MobileNavLink to="/register" onClick={() => setShowMobileMenu(false)}>
                  Kayıt Ol
                </MobileNavLink>
              </>
            )}
          </MobileMenu>
        )}
      </AnimatePresence>

    </Nav>
  );
};

