import React from 'react';
import { Routes, Route } from 'react-router-dom';
import { motion, AnimatePresence } from 'framer-motion';
import styled, { ThemeProvider } from 'styled-components';
import { useTheme } from './contexts/ThemeContext';
import { useAuth } from './contexts/AuthContext';

// Components
import Navbar from './components/Navbar';
import LoginNavbar from './components/LoginNavbar';
import Footer from './components/Footer';
import LoadingSpinner from './components/LoadingSpinner';
import Layout from './components/Layout';

// Pages
import Home from './pages/Home';
import Explore from './pages/Explore';
import Profile from './pages/Profile';
import Saved from './pages/Saved';
import Notifications from './pages/Notifications';
import WorkDetail from './pages/WorkDetail';
import Login from './pages/Login';
import Register from './pages/Register';
import Admin from './pages/Admin';
import AdminLogin from './pages/AdminLogin';
import NotFound from './pages/NotFound';

// Global styles
const GlobalStyles = styled.div`
  min-height: 100vh;
  background: ${props => props.theme.background};
  color: ${props => props.theme.text};
  transition: all 0.3s ease;
  position: relative;
  overflow-x: hidden;

  &::before {
    content: '';
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-image: 
      radial-gradient(circle at 1px 1px, rgba(255,255,255,0.1) 1px, transparent 0);
    background-size: 20px 20px;
    opacity: 0.3;
    z-index: -1;
  }
`;

const AppContainer = styled.div`
  min-height: 100vh;
  display: flex;
  flex-direction: column;
`;

const MainContent = styled.main`
  flex: 1;
  padding-top: ${props => props.isAuthPage ? '0' : '80px'}; /* No padding for auth pages */
`;

const pageVariants = {
  initial: { opacity: 0, y: 20 },
  in: { opacity: 1, y: 0 },
  out: { opacity: 0, y: -20 }
};

const pageTransition = {
  type: 'tween',
  ease: 'anticipate',
  duration: 0.4
};

function App() {
  const { theme } = useTheme();
  const { loading, user } = useAuth();
  const location = window.location.pathname;

  console.log('App - user:', user); // Debug log

  if (loading) {
    return <LoadingSpinner />;
  }

  // Show different navbar for login/register pages
  const isAuthPage = location === '/login' || location === '/register';
  const isAdminPage = location === '/admin' || location === '/admin-login';

  return (
    <ThemeProvider theme={theme}>
      <GlobalStyles>
        <AppContainer>
          {isAuthPage ? <LoginNavbar /> : isAdminPage ? null : <Navbar />}
          {isAdminPage ? (
            <AnimatePresence mode="wait">
              <Routes>
                <Route 
                  path="/admin-login" 
                  element={
                    <motion.div
                      initial="initial"
                      animate="in"
                      exit="out"
                      variants={pageVariants}
                      transition={pageTransition}
                    >
                      <AdminLogin />
                    </motion.div>
                  } 
                />
                <Route 
                  path="/admin" 
                  element={
                    <motion.div
                      initial="initial"
                      animate="in"
                      exit="out"
                      variants={pageVariants}
                      transition={pageTransition}
                    >
                      <Admin />
                    </motion.div>
                  } 
                />
              </Routes>
            </AnimatePresence>
          ) : (
            <MainContent user={user} isAuthPage={isAuthPage}>
            <AnimatePresence mode="wait">
              <Routes>
                <Route 
                  path="/" 
                  element={
                    <Layout>
                      <motion.div
                        initial="initial"
                        animate="in"
                        exit="out"
                        variants={pageVariants}
                        transition={pageTransition}
                      >
                        <Home />
                      </motion.div>
                    </Layout>
                  } 
                />
                <Route 
                  path="/explore" 
                  element={
                    <Layout>
                      <motion.div
                        initial="initial"
                        animate="in"
                        exit="out"
                        variants={pageVariants}
                        transition={pageTransition}
                      >
                        <Explore />
                      </motion.div>
                    </Layout>
                  } 
                />
                <Route 
                  path="/profile" 
                  element={
                    <Layout>
                      <motion.div
                        initial="initial"
                        animate="in"
                        exit="out"
                        variants={pageVariants}
                        transition={pageTransition}
                      >
                        <Profile />
                      </motion.div>
                    </Layout>
                  } 
                />
                <Route 
                  path="/profile/:username" 
                  element={
                    <Layout>
                      <motion.div
                        initial="initial"
                        animate="in"
                        exit="out"
                        variants={pageVariants}
                        transition={pageTransition}
                      >
                        <Profile />
                      </motion.div>
                    </Layout>
                  } 
                />
                <Route 
                  path="/saved" 
                  element={
                    <Layout>
                      <motion.div
                        initial="initial"
                        animate="in"
                        exit="out"
                        variants={pageVariants}
                        transition={pageTransition}
                      >
                        <Saved />
                      </motion.div>
                    </Layout>
                  } 
                />
                <Route 
                  path="/notifications" 
                  element={
                    <Layout>
                      <motion.div
                        initial="initial"
                        animate="in"
                        exit="out"
                        variants={pageVariants}
                        transition={pageTransition}
                      >
                        <Notifications />
                      </motion.div>
                    </Layout>
                  } 
                />
                <Route 
                  path="/work/:id" 
                  element={
                    <Layout>
                      <motion.div
                        initial="initial"
                        animate="in"
                        exit="out"
                        variants={pageVariants}
                        transition={pageTransition}
                      >
                        <WorkDetail />
                      </motion.div>
                    </Layout>
                  } 
                />
                <Route 
                  path="/login" 
                  element={
                    <motion.div
                      initial="initial"
                      animate="in"
                      exit="out"
                      variants={pageVariants}
                      transition={pageTransition}
                    >
                      <Login />
                    </motion.div>
                  } 
                />
                <Route 
                  path="/register" 
                  element={
                    <motion.div
                      initial="initial"
                      animate="in"
                      exit="out"
                      variants={pageVariants}
                      transition={pageTransition}
                    >
                      <Register />
                    </motion.div>
                  } 
                />
                <Route 
                  path="*" 
                  element={
                    <Layout>
                      <motion.div
                        initial="initial"
                        animate="in"
                        exit="out"
                        variants={pageVariants}
                        transition={pageTransition}
                      >
                        <NotFound />
                      </motion.div>
                    </Layout>
                  } 
                />
              </Routes>
            </AnimatePresence>
          </MainContent>
          )}
          {!isAdminPage && <Footer />}
        </AppContainer>
      </GlobalStyles>
    </ThemeProvider>
  );
}

export default App;
