import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { FiHeart, FiMessageCircle, FiCalendar, FiTrendingUp } from 'react-icons/fi';

const SidebarContainer = styled.div`
  width: 100%;
  background: transparent;
  padding: 0;
  display: flex;
  align-items: center;
  justify-content: space-between;

  @media (max-width: 1200px) {
    display: none;
  }
`;

const HeaderTitle = styled.div`
  display: flex;
  align-items: center;
  gap: 8px;
  font-size: 1rem;
  font-weight: 600;
  color: ${props => props.theme.text};
`;

const DropdownButton = styled.button`
  background: ${props => props.theme.surface};
  border: 1px solid ${props => props.theme.border};
  border-radius: 8px;
  padding: 8px 12px;
  color: ${props => props.theme.text};
  font-size: 0.9rem;
  cursor: pointer;
  transition: all 0.2s ease;
  display: flex;
  align-items: center;
  gap: 6px;

  &:hover {
    background: ${props => props.theme.surfaceHover};
  }
`;



const ArtistsSidebar = () => {
  // Mock data for artist articles
  const articles = [
    {
      id: 1,
      author: {
        name: "Zeynep Kaya",
        avatar: "ZK",
        isVerified: true
      },
      title: "Dijital Sanatta Yeni Dönem: AI ile Yaratıcılık",
      excerpt: "Yapay zeka teknolojilerinin sanat dünyasına etkisi ve yaratıcı süreçlerdeki rolü hakkında düşüncelerim...",
      date: "2 gün önce",
      likes: 124,
      comments: 23,
      isTrending: true
    },
    {
      id: 2,
      author: {
        name: "Mehmet Özkan",
        avatar: "MÖ",
        isVerified: true
      },
      title: "Minimalist Tasarımın Gücü",
      excerpt: "Sade ve etkili tasarım prensipleri ile nasıl daha güçlü görsel hikayeler anlatabiliriz?",
      date: "4 gün önce",
      likes: 89,
      comments: 15,
      isTrending: false
    },
    {
      id: 3,
      author: {
        name: "Ayşe Demir",
        avatar: "AD",
        isVerified: false
      },
      title: "Renk Teorisi ve Duygusal Tasarım",
      excerpt: "Renklerin psikolojik etkileri ve kullanıcı deneyimindeki önemi üzerine bir analiz...",
      date: "1 hafta önce",
      likes: 67,
      comments: 12,
      isTrending: false
    },
    {
      id: 4,
      author: {
        name: "Can Yılmaz",
        avatar: "CY",
        isVerified: true
      },
      title: "Sürdürülebilir Tasarım Yaklaşımları",
      excerpt: "Çevre dostu tasarım prensipleri ve gelecek nesillere bırakacağımız miras...",
      date: "1 hafta önce",
      likes: 156,
      comments: 31,
      isTrending: true
    }
  ];

  return (
    <SidebarContainer>
      <HeaderTitle>
        <FiTrendingUp size={18} />
        Sanatçı Köşe Yazıları
      </HeaderTitle>

      <DropdownButton>
        Son Yazılar
        <FiTrendingUp size={14} />
      </DropdownButton>
    </SidebarContainer>
  );
};

export default ArtistsSidebar;
