import React, { useState } from 'react';
import styled from 'styled-components';
import { FiHeart, FiEye, FiTrendingUp } from 'react-icons/fi';
import { useAuth } from '../contexts/AuthContext';
// import toast from 'react-hot-toast';

const Card = styled.div`
  background: ${props => props.theme.surface};
  border: none;
  border-radius: 12px;
  overflow: hidden;
  cursor: pointer;
  position: relative;
  height: auto;
  width: 420px;
  flex-shrink: 0;
  margin: 0;

  &:hover {
    transform: none;
    box-shadow: none;
  }

  @media (max-width: 768px) {
    width: 340px;
  }

  @media (max-width: 480px) {
    width: 320px;
  }
`;

const ImageContainer = styled.div`
  position: relative;
  width: 100%;
  height: 250px;
  overflow: hidden;
  background: #1a1a1a;
`;

const WorkImage = styled.img`
  width: 100%;
  height: 100%;
  object-fit: cover;
  transition: transform 0.3s ease;

  ${Card}:hover & {
    transform: scale(1.05);
  }
`;

const Overlay = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: linear-gradient(
    to bottom,
    rgba(0, 0, 0, 0) 0%,
    rgba(0, 0, 0, 0.7) 100%
  );
  opacity: 0;
  transition: opacity 0.3s ease;
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 16px;

  ${Card}:hover & {
    opacity: 1;
  }
`;

// Trend ikonu
const TrendIcon = styled.div`
  position: absolute;
  top: 12px;
  right: 12px;
  width: 28px;
  height: 28px;
  background: linear-gradient(135deg, #FF6B35, #F7931E);
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  color: white;
  font-size: 12px;
  z-index: 10;
  box-shadow: 0 2px 8px rgba(255, 107, 53, 0.3);
  cursor: pointer;
  transition: all 0.2s ease;

  &:hover {
    transform: scale(1.1);
    box-shadow: 0 4px 12px rgba(255, 107, 53, 0.4);
  }
`;





// Instagram benzeri post içeriği
const PostContent = styled.div`
  padding: 12px 16px;
  background: ${props => props.theme.surface};
  border-top: 1px solid #262626;
`;

const PostHeader = styled.div`
  display: flex;
  align-items: center;
  gap: 12px;
  margin-bottom: 12px;
`;

const UserAvatar = styled.div`
  width: 24px;
  height: 24px;
  border-radius: 50%;
  background: #FF6B35;
  display: flex;
  align-items: center;
  justify-content: center;
  color: white;
  font-weight: 300;
  font-size: 12px;
  flex-shrink: 0;
`;

const UserAvatarImg = styled.img`
  width: 30px;
  height: 30px;
  border-radius: 50%;
  object-fit: cover;
`;

const UserInfo = styled.div`
  flex: 1;
`;

const UserName = styled.div`
  font-weight: 600;
  color: ${props => props.theme.text};
  font-size: 14px;
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
`;

const PostActions = styled.div`
  display: flex;
  align-items: center;
  gap: 10px;
  margin-bottom: 1px;
`;

const ActionButton = styled.button`
  background: none;
  border: none;
  color: ${props => props.theme.text};
  font-size: 17px;
  cursor: pointer;
  padding: 0;
  display: flex;
  align-items: center;
  justify-content: center;
  transition: color 0.2s ease;

  &:hover {
    color: #FF6B35;
  }
`;

const LikesCount = styled.div`
  font-weight: 600;
  color: ${props => props.theme.text};
  font-size: 13px;
  margin-bottom: 4px;
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
`;

const PostTitle = styled.div`
  font-weight: 600;
  color: ${props => props.theme.text};
  font-size: 16px;
  line-height: 1.3;
  margin-bottom: 4px;
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
`;

const PostDescription = styled.div`
  color: #8e8e8e;
  font-size: 14px;
  line-height: 1.4;
  margin-bottom: 8px;
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
`;

const PostCaption = styled.div`
  color: ${props => props.theme.text};
  font-size: 14px;
  line-height: 1.4;
  margin-bottom: 8px;
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
`;

const PostTime = styled.div`
  color: #8e8e8e;
  font-size: 12px;
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
`;

const PostStats = styled.div`
  display: flex;
  align-items: center;
  justify-content: flex-end;
  gap: 16px;
  margin-top: 8px;
  padding-top: 8px;
`;

const StatItem = styled.div`
  display: flex;
  align-items: center;
  gap: 4px;
  color: #8e8e8e;
  font-size: 12px;
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
`;

const Title = styled.h3`
  font-size: 1.1rem;
  font-weight: 600;
  color: ${props => props.theme.text};
  margin-bottom: 6px;
  line-height: 1.2;
  display: -webkit-box;
  -webkit-line-clamp: 1;
  -webkit-box-orient: vertical;
  overflow: hidden;
`;

const Author = styled.div`
  display: flex;
  align-items: center;
  gap: 8px;
  margin-bottom: 8px;
`;

const AuthorAvatar = styled.div`
  width: 6px;
  height: 6px;
  border-radius: 50%;
  background: ${props => props.theme.gradient};
  display: flex;
  align-items: center;
  justify-content: center;
  color: ${props => props.theme.text};
  font-weight: 600;
  font-size: 0.2rem;
  border: none;
`;

const AuthorAvatarImg = styled.img`
  width: 100%;
  height: 100%;
  border-radius: 50%;
  object-fit: cover;
`;

const AuthorInfo = styled.div`
  flex: 1;
`;

const AuthorName = styled.div`
  font-weight: 500;
  color: ${props => props.theme.text};
  font-size: 0.8rem;
`;

const AuthorUsername = styled.div`
  color: rgba(255, 255, 255, 0.7);
  font-size: 0.7rem;
`;

const Stats = styled.div`
  display: flex;
  align-items: center;
  gap: 12px;
  margin-top: 8px;
`;


const Category = styled.div`
  display: inline-block;
  padding: 3px 8px;
  background: rgba(255, 255, 255, 0.2);
  color: ${props => props.theme.text};
  border-radius: 12px;
  font-size: 0.7rem;
  font-weight: 500;
  margin-bottom: 8px;
  backdrop-filter: blur(10px);
`;

const WorkCard = ({ work }) => {
  const { user } = useAuth();
  // const [isLiked, setIsLiked] = useState(work.likes?.includes(user?._id) || false);
  // const [isSaved, setIsSaved] = useState(user?.savedWorks?.includes(work._id) || false);
  const [likeCount] = useState(work.likeCount || work.likes?.length || 0);

