const ErrorMessage = styled.div`
  background: #fee2e2;
  color: #dc2626;
  padding: 12px 16px;
  border-radius: 8px;
  font-size: 0.9rem;
  border: 1px solid #fecaca;
`;

const Login = () => {
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });
  const [showPassword, setShowPassword] = useState(false);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const { login } = useAuth();
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
    if (error) setError('');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      const result = await login(formData.email, formData.password);
      if (result.success) {
        navigate('/');
      } else {
        setError(result.message || 'Giriş yapılırken bir hata oluştu');
      }
    } catch (err) {
      setError(err.message || 'Giriş yapılırken bir hata oluştu');
    }

    setLoading(false);
  };

  return (
    <Container>
      <LeafContainer>
        {[...Array(15)].map((_, i) => (
          <Leaf
            key={i}
            size={Math.random() * 20 + 10}
            color={`hsl(${Math.random() * 40 + 20}, 80%, 60%)`}
            left={Math.random() * 100}
            initial={{ y: -50, rotate: 0 }}
            animate={{
              y: [0, window.innerHeight + 50],
              rotate: [0, 360, 720],
              x: [0, Math.random() * 100 - 50, Math.random() * 100 - 50]
            }}
            transition={{
              duration: Math.random() * 10 + 10,
              delay: Math.random() * 5,
              repeat: Infinity,
              ease: "linear"
            }}
          />
        ))}
      </LeafContainer>

      <LeftSection>
        <ArtworkTitle>3D Sanat Dünyası</ArtworkTitle>
        <ArtworkSubtitle>
          Yaratıcılığınızı dijital dünyada hayata geçirin. 
          Üç boyutlu sanat eserlerinizi dünyayla paylaşın.
        </ArtworkSubtitle>

        <ArtworkContainer>
          <FloatingElement
            gradient="linear-gradient(135deg, #FF8C42, #FF6B35)"
            initial={{ y: 0, rotate: 0 }}
            animate={{ 
              y: [-10, 10, -10],
              rotate: [0, 5, -5, 0]
            }}
            transition={{
              duration: 4,
              repeat: Infinity,
              ease: "easeInOut"
            }}
          />
          <FloatingElement
            gradient="linear-gradient(135deg, #D2691E, #CD853F)"
            initial={{ y: 0, rotate: 0 }}
            animate={{ 
              y: [10, -10, 10],
              rotate: [0, -3, 3, 0]
            }}
            transition={{
              duration: 5,
              repeat: Infinity,
              ease: "easeInOut"
            }}
          />
          <FloatingElement
            gradient="linear-gradient(135deg, #CD853F, #DEB887)"
            initial={{ y: 0, rotate: 0 }}
            animate={{ 
              y: [-5, 15, -5],
              rotate: [0, 2, -2, 0]
            }}
            transition={{
              duration: 6,
              repeat: Infinity,
              ease: "easeInOut"
            }}
          />
          <FloatingElement
            gradient="linear-gradient(135deg, #FF6B35, #FF8C42)"
            initial={{ y: 0, rotate: 0 }}
            animate={{ 
              y: [15, -5, 15],
              rotate: [0, -4, 4, 0]
            }}
            transition={{
              duration: 7,
              repeat: Infinity,
              ease: "easeInOut"
            }}
          />
        </ArtworkContainer>
      </LeftSection>

      <RightSection>
        <LoginCard
          initial={{ opacity: 0, y: 30 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
        >
          <Logo>
            <LogoIcon>F</LogoIcon>
            <LogoText>Feellink</LogoText>
            <LogoSubtext>Hesabınıza giriş yapın</LogoSubtext>
          </Logo>

          <Form onSubmit={handleSubmit}>
            {error && <ErrorMessage>{error}</ErrorMessage>}

            <FormGroup>
              <Label htmlFor="email">E-posta</Label>
              <InputContainer>
                <Input
                  type="email"
                  id="email"
                  name="email"
                  placeholder="ornek@email.com"
                  value={formData.email}
                  onChange={handleChange}
                  required
                />
                <FiMail style={{ position: 'absolute', right: '16px', top: '50%', transform: 'translateY(-50%)', color: '#999' }} />
              </InputContainer>
            </FormGroup>

            <FormGroup>
              <Label htmlFor="password">Şifre</Label>
              <PasswordContainer>
                <Input
                  type={showPassword ? 'text' : 'password'}
                  id="password"
                  name="password"
                  placeholder="Şifrenizi girin"
                  value={formData.password}
                  onChange={handleChange}
                  required
                />
                <PasswordToggle
                  type="button"
                  onClick={() => setShowPassword(!showPassword)}
                >
                  {showPassword ? <FiEyeOff size={20} /> : <FiEye size={20} />}
                </PasswordToggle>
              </PasswordContainer>
            </FormGroup>

            <SubmitButton type="submit" disabled={loading}>
              {loading ? 'Giriş yapılıyor...' : 'Giriş Yap'}
              <FiArrowRight size={18} />
            </SubmitButton>
          </Form>

          <Divider>
            <span>veya</span>
          </Divider>

          <SignupLink>
            Hesabınız yok mu?{' '}
            <Link to="/register">Kayıt olun</Link>
          </SignupLink>
        </LoginCard>
      </RightSection>
    </Container>
  );
};

export default Login;
