import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { FiMail, FiLock, FiEye, FiEyeOff, FiArrowRight, FiSun, FiMoon } from 'react-icons/fi';
import { useAuth } from '../contexts/AuthContext';
import { useTheme } from '../contexts/ThemeContext';

const Container = styled.div`
  min-height: 100vh;
  background: ${props => props.theme.background};
  display: flex;
  flex-direction: column;
  position: relative;
  overflow: hidden;
`;

const Header = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 20px 40px;
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  z-index: 10;
`;

const Logo = styled.div`
  display: flex;
  align-items: center;
  gap: 12px;
`;

const LogoIcon = styled.div`
  width: 40px;
  height: 40px;
  background: ${props => props.theme.primary};
  border-radius: 8px;
  display: flex;
  align-items: center;
  justify-content: center;
  color: white;
  font-size: 20px;
  font-weight: 800;
`;

const LogoText = styled.h1`
  font-size: 1.5rem;
  font-weight: 800;
  color: ${props => props.theme.text};
`;

const ThemeToggle = styled.button`
  background: ${props => props.theme.cardBackground};
  border: 1px solid ${props => props.theme.border};
  border-radius: 8px;
  padding: 8px;
  color: ${props => props.theme.text};
  cursor: pointer;
  transition: all 0.3s ease;
  display: flex;
  align-items: center;
  justify-content: center;

  &:hover {
    background: ${props => props.theme.primary};
    color: white;
  }
`;

const MainContent = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 20px 200px 20px 20px;
  flex: 1;
  margin-top: 80px;
`;

const LeafContainer = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  pointer-events: none;
  z-index: 1;
`;

const Leaf = styled(motion.div)`
  position: absolute;
  width: ${props => props.size}px;
  height: ${props => props.size}px;
  background: ${props => props.color};
  border-radius: 0 100% 0 100%;
  opacity: 0.8;
  top: -10px;
  left: ${props => props.left}%;
`;

const LeftSection = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  padding: 40px;
  position: relative;
  z-index: 1;
  height: 100vh;
`;

const ArtworkContainer = styled.div`
  position: relative;
  width: 100%;
  max-width: 500px;
  height: 400px;
  display: flex;
  align-items: center;
  justify-content: center;
`;

const FloatingElement = styled(motion.div)`
  position: absolute;
  background: ${props => props.color};
  border-radius: ${props => props.radius};
  opacity: 0.8;
  filter: blur(${props => props.blur}px);
`;

const ArtworkTitle = styled.h2`
  font-size: 3rem;
  font-weight: 900;
  background: linear-gradient(45deg, #FF6B35, #F7931E, #FFD700, #FF8C42);
  background-size: 300% 300%;
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  margin-top: 60px;
  margin-bottom: 20px;
  text-align: center;
  animation: gradientShift 2s ease-in-out infinite, pulse 1.5s ease-in-out infinite;
  text-shadow: 0 0 50px rgba(255, 107, 53, 0.8), 0 0 100px rgba(255, 107, 53, 0.6);

  @keyframes gradientShift {
    0%, 100% { background-position: 0% 50%; }
    50% { background-position: 100% 50%; }
  }

  @keyframes pulse {
    0%, 100% { 
      transform: scale(1);
      filter: brightness(1);
    }
    50% { 
      transform: scale(1.05);
      filter: brightness(1.3);
    }
  }
`;

const ArtworkSubtitle = styled.p`
  font-size: 1.4rem;
  font-weight: 600;
  color: #FFFFFF;
  text-align: center;
  max-width: 450px;
  line-height: 1.8;
  text-shadow: 0 2px 10px rgba(0, 0, 0, 0.5);
`;

const RightSection = styled.div`
  flex: 0 0 400px;
  display: flex;
  align-items: flex-start;
  justify-content: center;
  padding: 20px;
  height: 100vh;
  padding-top: 160px;
`;

const LoginCard = styled(motion.div)`
  background: ${props => props.theme.surface};
  border: 1px solid ${props => props.theme.border};
  border-radius: 24px;
  padding: 48px;
  width: 100%;
  max-width: 400px;
  box-shadow: 0 20px 60px ${props => props.theme.shadow};
`;


const Form = styled.form`
  display: flex;
  flex-direction: column;
  gap: 24px;
`;

const FormGroup = styled.div`
  position: relative;
`;

const Label = styled.label`
  display: block;
  font-weight: 600;
  color: ${props => props.theme.text};
  margin-bottom: 8px;
  font-size: 0.9rem;
`;

const InputContainer = styled.div`
  position: relative;
`;

const Input = styled.input`
  width: 100%;
  padding: 16px 20px 16px 52px;
  border: 2px solid ${props => props.theme.border};
  border-radius: 12px;
  background: #FFFFFF;
  color: ${props => props.theme.text};
  font-size: 16px;
  transition: all 0.3s ease;

  &:focus {
    outline: none;
    border-color: ${props => props.theme.primary};
    box-shadow: 0 0 0 3px ${props => props.theme.primaryLight};
  }

  &::placeholder {
    color: ${props => props.theme.textMuted};
  }
`;

const InputIcon = styled.div`
  position: absolute;
  left: 18px;
  top: 50%;
  transform: translateY(-50%);
  color: ${props => props.theme.textMuted};
  font-size: 20px;
`;

const PasswordToggle = styled.button`
  position: absolute;
  right: 18px;
  top: 50%;
  transform: translateY(-50%);
  background: none;
  border: none;
  color: ${props => props.theme.textMuted};
  cursor: pointer;
  padding: 4px;
  transition: color 0.3s ease;

  &:hover {
    color: ${props => props.theme.primary};
  }
`;

const SubmitButton = styled.button`
  width: 100%;
  padding: 16px;
  background: ${props => props.theme.primary};
  color: white;
  border: none;
  border-radius: 12px;
  font-size: 16px;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s ease;
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 8px;

  &:hover {
    background: ${props => props.theme.primaryHover};
    transform: translateY(-2px);
    box-shadow: 0 10px 30px rgba(255, 107, 53, 0.3);
  }

  &:disabled {
    opacity: 0.6;
    cursor: not-allowed;
    transform: none;
  }
`;

const Divider = styled.div`
  display: flex;
  align-items: center;
  margin: 32px 0;
  color: ${props => props.theme.textMuted};
  font-size: 0.9rem;

  &::before,
  &::after {
    content: '';
    flex: 1;
    height: 1px;
    background: ${props => props.theme.border};
  }

  &::before {
    margin-right: 16px;
  }

  &::after {
    margin-left: 16px;
  }
`;

const SignupLink = styled.div`
  text-align: center;
  color: ${props => props.theme.textSecondary};
  font-size: 0.9rem;

  a {
    color: ${props => props.theme.primary};
    text-decoration: none;
    font-weight: 600;
    transition: color 0.3s ease;

    &:hover {
      color: ${props => props.theme.primaryHover};
    }
  }
`;

const ErrorMessage = styled.div`
  background: ${props => props.theme.error}20;
  border: 1px solid ${props => props.theme.error};
  color: ${props => props.theme.error};
  padding: 12px 16px;
  border-radius: 8px;
  font-size: 0.9rem;
  margin-bottom: 16px;
`;

const Login = () => {
  const navigate = useNavigate();
  const { login } = useAuth();
  const { theme, toggleTheme } = useTheme();
  const [formData, setFormData] = useState({
    email: '',
    password: '',
  });
  const [showPassword, setShowPassword] = useState(false);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
    setError('');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    if (!formData.email || !formData.password) {
      setError('Tüm alanları doldurun');
      setLoading(false);
      return;
    }

    const result = await login(formData.email, formData.password);

    if (result.success) {
      navigate('/');
    } else {
      setError(result.message || 'Giriş yapılırken hata oluştu');
    }

    setLoading(false);
  };

  return (
    <Container>
      <Header>
        <Logo>
          <LogoIcon>F</LogoIcon>
          <LogoText>Feellink</LogoText>
        </Logo>
        <ThemeToggle onClick={toggleTheme}>
          {theme === 'light' ? <FiMoon size={20} /> : <FiSun size={20} />}
        </ThemeToggle>
      </Header>

      <LeafContainer>
        {[...Array(15)].map((_, i) => (
          <Leaf
            key={i}
            size={Math.random() * 20 + 10}
            color={`hsl(${Math.random() * 40 + 20}, 80%, 60%)`}
            left={Math.random() * 100}
            initial={{ y: -50, rotate: 0 }}
            animate={{
              y: [0, window.innerHeight + 50],
              rotate: [0, 360, 720],
              x: [0, Math.random() * 100 - 50, Math.random() * 100 - 50]
            }}
            transition={{
              duration: Math.random() * 10 + 10,
              delay: Math.random() * 5,
              repeat: Infinity,
              ease: "linear"
            }}
          />
        ))}
      </LeafContainer>

      <MainContent>
        <LeftSection>
        <ArtworkTitle>3D Sanat Dünyası</ArtworkTitle>
        <ArtworkSubtitle>
          Yaratıcılığınızı dijital dünyada hayata geçirin. 
          Üç boyutlu sanat eserlerinizi dünyayla paylaşın.
        </ArtworkSubtitle>

        <ArtworkContainer>
          <FloatingElement
            color="linear-gradient(45deg, #FF6B35, #F7931E)"
            radius="50%"
            blur="0"
            width="120px"
            height="120px"
            top="20%"
            left="10%"
            animate={{
              y: [0, -20, 0],
              rotate: [0, 180, 360],
            }}
            transition={{
              duration: 8,
              repeat: Infinity,
              ease: "easeInOut"
            }}
          />

          <FloatingElement
            color="linear-gradient(45deg, #4F46E5, #7C3AED)"
            radius="20px"
            blur="2"
            width="80px"
            height="80px"
            top="60%"
            left="70%"
            animate={{
              y: [0, 20, 0],
              x: [0, 10, 0],
              rotate: [0, -90, 0],
            }}
            transition={{
              duration: 6,
              repeat: Infinity,
              ease: "easeInOut"
            }}
          />

          <FloatingElement
            color="linear-gradient(45deg, #10B981, #059669)"
            radius="30px"
            blur="1"
            width="100px"
            height="100px"
            top="40%"
            left="50%"
            animate={{
              y: [0, -15, 0],
              x: [0, -15, 0],
              rotate: [0, 270, 0],
            }}
            transition={{
              duration: 10,
              repeat: Infinity,
              ease: "easeInOut"
            }}
          />

          <FloatingElement
            color="linear-gradient(45deg, #EF4444, #DC2626)"
            radius="40px"
            blur="3"
            width="60px"
            height="60px"
            top="10%"
            left="80%"
            animate={{
              y: [0, 25, 0],
              rotate: [0, -180, 0],
            }}
            transition={{
              duration: 7,
              repeat: Infinity,
              ease: "easeInOut"
            }}
          />
        </ArtworkContainer>
        </LeftSection>

        <RightSection>
        <LoginCard
          initial={{ opacity: 0, y: 30 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
        >
        <Logo>
          <LogoIcon>F</LogoIcon>
          <LogoText>Feellink</LogoText>
          <LogoSubtext>Hesabınıza giriş yapın</LogoSubtext>
        </Logo>

        <Form onSubmit={handleSubmit}>
          {error && <ErrorMessage>{error}</ErrorMessage>}

          <FormGroup>
            <Label htmlFor="email">E-posta</Label>
            <InputContainer>
              <Input
                type="email"
                id="email"
                name="email"
                placeholder="ornek@email.com"
                value={formData.email}
                onChange={handleChange}
                required
              />
              <InputIcon>
                <FiMail />
              </InputIcon>
            </InputContainer>
          </FormGroup>

          <FormGroup>
            <Label htmlFor="password">Şifre</Label>
            <InputContainer>
              <Input
                type={showPassword ? 'text' : 'password'}
                id="password"
                name="password"
                placeholder="Şifrenizi girin"
                value={formData.password}
                onChange={handleChange}
                required
              />
              <InputIcon>
                <FiLock />
              </InputIcon>
              <PasswordToggle
                type="button"
                onClick={() => setShowPassword(!showPassword)}
              >
                {showPassword ? <FiEyeOff size={20} /> : <FiEye size={20} />}
              </PasswordToggle>
            </InputContainer>
          </FormGroup>

          <SubmitButton type="submit" disabled={loading}>
            {loading ? 'Giriş yapılıyor...' : 'Giriş Yap'}
            {!loading && <FiArrowRight size={18} />}
          </SubmitButton>
        </Form>

        <Divider>veya</Divider>

        <SignupLink>
          Hesabınız yok mu?{' '}
          <Link to="/register">Kayıt olun</Link>
        </SignupLink>
        </LoginCard>
        </RightSection>
      </MainContent>
    </Container>
  );
};

export default Login;
