          <Tab 
            theme={theme} 
            active={activeTab === 'posts'}
            onClick={() => setActiveTab('posts')}
          >
            <FiGrid size={12} />
            Eserler
          </Tab>
          <Tab 
            theme={theme} 
            active={activeTab === 'saved'}
            onClick={() => setActiveTab('saved')}
          >
            <FiBookmark size={12} />
            Kaydedilenler
          </Tab>
        </ProfileTabs>

        {activeTab === 'posts' && (
          <>
            {works.length > 0 ? (
              <WorksGrid>
                {works.map((work) => (
                  <WorkItem 
                    key={work._id || work.id} 
                    theme={theme}
                    onClick={() => handleWorkClick(work._id || work.id)}
                  >
                    <WorkImage 
                      src={work.imageUrl || work.image || work.images?.[0]?.url || ''} 
                      alt={`Eser ${work._id || work.id}`}
                      onError={(e) => {
                        e.target.style.background = '#000';
                        e.target.style.display = 'flex';
                        e.target.style.alignItems = 'center';
                        e.target.style.justifyContent = 'center';
                        e.target.style.color = '#666';
                        e.target.style.fontSize = '14px';
                        e.target.src = '';
                      }}
                    />
                    <WorkOverlay>
                      <OverlayIcon>
                        <FiHeart size={16} />
                        {work.likes}
                      </OverlayIcon>
                      <OverlayIcon>
                        <FiMessageCircle size={16} />
                        {work.comments?.length || work.comments}
                      </OverlayIcon>
                    </WorkOverlay>
                  </WorkItem>
                ))}
              </WorksGrid>
            ) : (
              <EmptyState theme={theme}>
                <EmptyIcon>📷</EmptyIcon>
                <EmptyTitle theme={theme}>Henüz eser yok</EmptyTitle>
                <EmptyDescription theme={theme}>
                  {isOwnProfile 
                    ? 'İlk eserinizi paylaşmaya ne dersiniz?'
                    : 'Bu kullanıcı henüz eser paylaşmamış.'
                  }
                </EmptyDescription>
              </EmptyState>
            )}
          </>
        )}

        {activeTab === 'saved' && (
          <EmptyState theme={theme}>
            <EmptyIcon>🔖</EmptyIcon>
            <EmptyTitle theme={theme}>Kaydedilen eserler</EmptyTitle>
            <EmptyDescription theme={theme}>
              Kaydettiğiniz eserler burada görünecek.
            </EmptyDescription>
          </EmptyState>
        )}
            </ProfileContainer>
          </ContentInner>
        </Content>
      </MainLayout>

      {/* Fotoğraf Yükleme Modalı */}
      {showPhotoUpload && (
        <PhotoUploadModal onClick={handleCancelPhoto}>
          <PhotoUploadContent theme={theme} onClick={(e) => e.stopPropagation()}>
            <PhotoUploadTitle theme={theme}>Profil Fotoğrafı Değiştir</PhotoUploadTitle>

            <PhotoPreview theme={theme}>
              {previewUrl ? (
                <PhotoPreviewImg src={previewUrl} alt="Önizleme" />
              ) : (
                <div style={{ color: theme.text, fontSize: '3rem' }}>
                  <FiCamera />
                </div>
              )}
            </PhotoPreview>

            <HiddenFileInput
              ref={fileInputRef}
              type="file"
              accept="image/*"
              onChange={handleFileSelect}
            />

            <PhotoUploadActions>
              <PhotoUploadButton2 
                theme={theme} 
                onClick={() => fileInputRef.current?.click()}
              >
                Fotoğraf Seç
              </PhotoUploadButton2>

              {previewUrl && (
                <PhotoUploadButton2 
                  theme={theme} 
                  primary 
                  onClick={handleSavePhoto}
                >
                  Kaydet
                </PhotoUploadButton2>
              )}

              <PhotoUploadButton2 
                theme={theme} 
                onClick={handleCancelPhoto}
              >
                İptal
              </PhotoUploadButton2>
            </PhotoUploadActions>
          </PhotoUploadContent>
        </PhotoUploadModal>
      )}

      {/* Eser Ekleme Modalı */}
      {showWorkUpload && (
        <WorkUploadModal onClick={handleCancelWork}>
          <WorkUploadContent theme={theme} onClick={(e) => e.stopPropagation()}>
            <WorkUploadTitle theme={theme}>Yeni Eser Ekle</WorkUploadTitle>

            <WorkForm>
              <WorkInput
                theme={theme}
                type="text"
                placeholder="Eser Başlığı"
                value={workTitle}
                onChange={(e) => setWorkTitle(e.target.value)}
              />

              <WorkTextarea
                theme={theme}
                placeholder="Eser Açıklaması (İsteğe bağlı)"
                value={workDescription}
                onChange={(e) => setWorkDescription(e.target.value)}
              />

              <WorkSelect
                theme={theme}
                value={workCategory}
                onChange={(e) => setWorkCategory(e.target.value)}
              >
                <option value="">Kategori Seçin</option>
                <option value="resim">Resim</option>
                <option value="heykel">Heykel</option>
                <option value="fotograf">Fotoğraf</option>
                <option value="dijital">Dijital Sanat</option>
                <option value="seramik">Seramik</option>
                <option value="diger">Diğer</option>
              </WorkSelect>

              {workPreviewUrl && (
                <WorkPreview>
                  <WorkPreviewImg src={workPreviewUrl} alt="Eser Önizleme" />
                </WorkPreview>
              )}
            </WorkForm>

            <WorkFileInput
              ref={workFileInputRef}
              type="file"
              accept="image/*"
              onChange={handleWorkFileSelect}
            />

            <WorkUploadActions>
              <WorkUploadButton 
                theme={theme} 
                onClick={() => workFileInputRef.current?.click()}
              >
                Dosya Seç
              </WorkUploadButton>

              {workTitle && workFile && (
                <WorkUploadButton 
                  theme={theme} 
                  primary 
                  onClick={handleSaveWork}
                >
                  Eser Ekle
                </WorkUploadButton>
              )}

              <WorkUploadButton 
                theme={theme} 
                onClick={handleCancelWork}
              >
                İptal
              </WorkUploadButton>
            </WorkUploadActions>
          </WorkUploadContent>
        </WorkUploadModal>
      )}
    </Container>
  );
};

export default Profile;
