import React, { useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { useTheme } from '../contexts/ThemeContext';
import { 
  FiMapPin, 
  FiGlobe, 
  FiUsers, 
  FiSettings,
  FiPlus,
  FiGrid,
  FiList,
  FiHeart,
  FiMessageCircle,
  FiShare2,
  FiMoreHorizontal,
  FiEdit3,
  FiBookmark,
  FiUserPlus,
  FiUserMinus
} from 'react-icons/fi';
import WorkCard from '../components/WorkCard';
import LoadingSpinner from '../components/LoadingSpinner';
import { useAuth } from '../contexts/AuthContext';

const Container = styled.div`
  min-height: 100vh;
  background: ${props => props.theme.background};
  padding-top: 80px;
`;

const ProfileContainer = styled.div`
  max-width: 935px;
  margin: 0 auto;
  padding: 30px 20px;
`;

const ProfileHeader = styled.div`
  display: flex;
  gap: 30px;
  margin-bottom: 44px;
  padding-bottom: 30px;
  border-bottom: 1px solid ${props => props.theme.border};

  @media (max-width: 768px) {
    flex-direction: column;
    align-items: center;
    text-align: center;
    gap: 20px;
  }
`;

const AvatarSection = styled.div`
  display: flex;
  justify-content: center;
`;

const Avatar = styled.div`
  width: 150px;
  height: 150px;
  border-radius: 50%;
  background: ${props => props.theme.gradient};
  display: flex;
  align-items: center;
  justify-content: center;
  color: white;
  font-size: 4rem;
  font-weight: 700;
  border: 3px solid ${props => props.theme.border};
  flex-shrink: 0;

  @media (max-width: 768px) {
    width: 120px;
    height: 120px;
    font-size: 3rem;
  }
`;

const AvatarImg = styled.img`
  width: 100%;
  height: 100%;
  border-radius: 50%;
  object-fit: cover;
  border: 3px solid ${props => props.theme.border};
`;

const ProfileInfo = styled.div`
  flex: 1;
  min-width: 0;
`;

const ProfileTop = styled.div`
  display: flex;
  align-items: center;
  gap: 20px;
  margin-bottom: 20px;
  flex-wrap: wrap;

  @media (max-width: 768px) {
    justify-content: center;
  }
`;

const Username = styled.h1`
  font-size: 1.75rem;
  font-weight: 300;
  color: ${props => props.theme.text};
  margin: 0;
  line-height: 1.2;

  @media (max-width: 768px) {
    font-size: 1.5rem;
  }
`;

const ActionButtons = styled.div`
  display: flex;
  gap: 8px;
  align-items: center;
`;

const ActionButton = styled.button`
  padding: 8px 16px;
  border-radius: 8px;
  border: 1px solid ${props => props.theme.border};
  background: ${props => props.primary ? props.theme.primary : props.theme.surface};
  color: ${props => props.primary ? 'white' : props.theme.text};
  font-weight: 600;
  font-size: 0.9rem;
  cursor: pointer;
  transition: all 0.3s ease;
  display: flex;
  align-items: center;
  gap: 6px;

  &:hover {
    background: ${props => props.primary ? props.theme.primaryHover : props.theme.surfaceHover};
  }
`;

const MoreButton = styled.button`
  padding: 8px;
  border-radius: 8px;
  border: 1px solid ${props => props.theme.border};
  background: ${props => props.theme.surface};
  color: ${props => props.theme.text};
  cursor: pointer;
  transition: all 0.3s ease;
  display: flex;
  align-items: center;
  justify-content: center;

  &:hover {
    background: ${props => props.theme.surfaceHover};
  }
`;

const Stats = styled.div`
  display: flex;
  gap: 40px;
  margin-bottom: 20px;

  @media (max-width: 768px) {
    justify-content: center;
    gap: 30px;
  }
`;

const StatItem = styled.div`
  text-align: left;

  @media (max-width: 768px) {
    text-align: center;
  }
`;

const StatNumber = styled.div`
  font-size: 1.1rem;
  font-weight: 600;
  color: ${props => props.theme.text};
  margin-bottom: 2px;
`;

const StatLabel = styled.div`
  font-size: 0.9rem;
  color: ${props => props.theme.textSecondary};
`;

const Bio = styled.div`
  color: ${props => props.theme.text};
  line-height: 1.5;
  margin-bottom: 12px;
`;

const BioText = styled.p`
  margin: 0 0 8px 0;
  font-size: 0.95rem;
`;

const BioLink = styled.a`
  color: ${props => props.theme.primary};
  text-decoration: none;
  font-weight: 500;

  &:hover {
    text-decoration: underline;
  }
`;

const ProfileTabs = styled.div`
  display: flex;
  justify-content: center;
  border-top: 1px solid ${props => props.theme.border};
  margin-top: 0;
`;

const Tab = styled.button`
  display: flex;
  align-items: center;
  gap: 6px;
  padding: 16px 0;
  background: none;
  border: none;
  color: ${props => props.active ? props.theme.text : props.theme.textSecondary};
  font-weight: 600;
  font-size: 0.9rem;
  cursor: pointer;
  border-top: 1px solid ${props => props.active ? props.theme.text : 'transparent'};
  transition: all 0.3s ease;
  margin-right: 60px;
  text-transform: uppercase;
  letter-spacing: 0.5px;

  &:last-child {
    margin-right: 0;
  }

  &:hover {
    color: ${props => props.theme.text};
  }
`;

const WorksGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 3px;
  margin-top: 0;

  @media (max-width: 768px) {
    gap: 2px;
  }
`;

const WorkItem = styled.div`
  aspect-ratio: 1;
  background: ${props => props.theme.surface};
  border: 1px solid ${props => props.theme.border};
  position: relative;
  cursor: pointer;
  overflow: hidden;
  transition: all 0.3s ease;

  &:hover {
    transform: scale(1.02);
    z-index: 10;
    box-shadow: 0 8px 25px ${props => props.theme.shadow};
  }
`;

const WorkImage = styled.img`
  width: 100%;
  height: 100%;
  object-fit: cover;
  transition: all 0.3s ease;

  ${WorkItem}:hover & {
    transform: scale(1.1);
  }
`;

const WorkOverlay = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: rgba(0, 0, 0, 0.5);
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 20px;
  opacity: 0;
  transition: all 0.3s ease;

  ${WorkItem}:hover & {
    opacity: 1;
  }
`;

const OverlayIcon = styled.div`
  color: white;
  font-size: 1.2rem;
  display: flex;
  align-items: center;
  gap: 4px;
  font-weight: 600;
  font-size: 0.9rem;
`;

const EmptyState = styled.div`
  text-align: center;
  padding: 60px 20px;
  color: ${props => props.theme.textSecondary};
`;

const EmptyIcon = styled.div`
  font-size: 3rem;
  margin-bottom: 16px;
`;

const EmptyTitle = styled.h3`
  font-size: 1.3rem;
  font-weight: 600;
  margin-bottom: 8px;
  color: ${props => props.theme.text};
`;

const EmptyDescription = styled.p`
  font-size: 0.9rem;
  max-width: 300px;
  margin: 0 auto;
  line-height: 1.4;
`;

const Profile = () => {
  const { username } = useParams();
  const navigate = useNavigate();
  const { user: currentUser, followUser, unfollowUser } = useAuth();
  const [activeTab, setActiveTab] = useState('works');
  const [viewMode, setViewMode] = useState('grid');
  const [isFollowing, setIsFollowing] = useState(false);

  // Fetch profile data
  const { data: profileData, isLoading } = useQuery(
    ['profile', username],
    async () => {
      const response = await axios.get(`/api/users/profile/${username}`);
      return response.data;
    },
    {
      enabled: !!username,
    }
  );

  const profile = profileData?.user;
  const works = profileData?.works || [];

  const handleFollow = async () => {
    if (!currentUser) {
      toast.error('Takip etmek için giriş yapmalısınız');
      return;
    }

    if (isFollowing) {
      const result = await unfollowUser(profile._id);
      if (result.success) {
        setIsFollowing(false);
      }
    } else {
      const result = await followUser(profile._id);
      if (result.success) {
        setIsFollowing(true);
      }
    }
  };

  const isOwnProfile = currentUser && currentUser.username === username;

  if (isLoading) {
    return <LoadingSpinner text="Profil yükleniyor..." />;
  }

  if (!profile) {
    return (
      <Container>
        <Content>
          <EmptyState>
            <EmptyIcon>👤</EmptyIcon>
            <EmptyTitle>Kullanıcı bulunamadı</EmptyTitle>
            <EmptyDescription>
              Aradığınız kullanıcı bulunamadı veya hesabı silinmiş olabilir.
            </EmptyDescription>
          </EmptyState>
        </Content>
      </Container>
    );
  }

  return (
    <Container>
      <Header>
        <HeaderContent>
          <ProfileInfo>
            <AvatarContainer>
              {profile.avatar ? (
                <AvatarImg src={profile.avatar} alt={profile.fullName} />
              ) : (
                <Avatar>
                  {profile.fullName.charAt(0).toUpperCase()}
                </Avatar>
              )}
            </AvatarContainer>

            <ProfileDetails>
              <Name>{profile.fullName}</Name>
              <Username>@{profile.username}</Username>

              {profile.bio && <Bio>{profile.bio}</Bio>}

              {profile.location && (
                <Location>
                  <FiMapPin size={18} />
                  {profile.location}
                </Location>
              )}

              {profile.website && (
                <Website href={profile.website} target="_blank" rel="noopener noreferrer">
                  <FiGlobe size={18} />
                  {profile.website}
                </Website>
              )}

              <Stats>
                <StatItem>
                  <StatNumber>{works.length}</StatNumber>
                  <StatLabel>Eser</StatLabel>
                </StatItem>
                <StatItem>
                  <StatNumber>{profile.followers}</StatNumber>
                  <StatLabel>Takipçi</StatLabel>
                </StatItem>
                <StatItem>
                  <StatNumber>{profile.following}</StatNumber>
                  <StatLabel>Takip</StatLabel>
                </StatItem>
              </Stats>

              <Actions>
                {isOwnProfile ? (
                  <>
                    <ActionButton onClick={() => navigate('/settings')}>
                      <FiSettings size={18} />
                      Ayarlar
                    </ActionButton>
                    <ActionButton primary>
                      <FiPlus size={18} />
                      Eser Ekle
                    </ActionButton>
                  </>
                ) : (
                  <ActionButton
                    primary={!isFollowing}
                    onClick={handleFollow}
                  >
                    <FiUsers size={18} />
                    {isFollowing ? 'Takibi Bırak' : 'Takip Et'}
                  </ActionButton>
                )}
              </Actions>
            </ProfileDetails>
          </ProfileInfo>
        </HeaderContent>
      </Header>

      <Content>
        <Tabs>
          <Tab
            active={activeTab === 'works'}
            onClick={() => setActiveTab('works')}
          >
            Eserler ({works.length})
          </Tab>
          <Tab
            active={activeTab === 'saved'}
            onClick={() => setActiveTab('saved')}
          >
            Kaydedilenler
          </Tab>
          <Tab
            active={activeTab === 'liked'}
            onClick={() => setActiveTab('liked')}
          >
            Beğenilenler
          </Tab>
        </Tabs>

        {activeTab === 'works' && (
          <>
            <ViewControls>
              <div></div>
              <ViewToggle>
                <ViewButton
                  active={viewMode === 'grid'}
                  onClick={() => setViewMode('grid')}
                >
                  <FiGrid size={18} />
                  Grid
                </ViewButton>
                <ViewButton
                  active={viewMode === 'list'}
                  onClick={() => setViewMode('list')}
                >
                  <FiList size={18} />
                  Liste
                </ViewButton>
              </ViewToggle>
            </ViewControls>

            {works.length > 0 ? (
              viewMode === 'grid' ? (
                <WorksGrid>
                  {works.map((work, index) => (
                    <motion.div
                      key={work._id}
                      initial={{ opacity: 0, y: 30 }}
                      animate={{ opacity: 1, y: 0 }}
                      transition={{ duration: 0.6, delay: index * 0.05 }}
                    >
                      <WorkCard work={work} />
                    </motion.div>
                  ))}
                </WorksGrid>
              ) : (
                <WorksList>
                  {works.map((work, index) => (
                    <motion.div
                      key={work._id}
                      initial={{ opacity: 0, x: -30 }}
                      animate={{ opacity: 1, x: 0 }}
                      transition={{ duration: 0.6, delay: index * 0.05 }}
                    >
                      <WorkCard work={work} />
                    </motion.div>
                  ))}
                </WorksList>
              )
            ) : (
              <EmptyState>
                <EmptyIcon>🎨</EmptyIcon>
                <EmptyTitle>Henüz eser yok</EmptyTitle>
                <EmptyDescription>
                  {isOwnProfile 
                    ? 'İlk eserinizi paylaşmaya ne dersiniz?'
                    : 'Bu kullanıcı henüz eser paylaşmamış.'
                  }
                </EmptyDescription>
              </EmptyState>
            )}
          </>
        )}

        {activeTab === 'saved' && (
          <EmptyState>
            <EmptyIcon>🔖</EmptyIcon>
            <EmptyTitle>Kaydedilen eserler</EmptyTitle>
            <EmptyDescription>
              Bu özellik yakında eklenecek.
            </EmptyDescription>
          </EmptyState>
        )}

        {activeTab === 'liked' && (
          <EmptyState>
            <EmptyIcon>❤️</EmptyIcon>
            <EmptyTitle>Beğenilen eserler</EmptyTitle>
            <EmptyDescription>
              Bu özellik yakında eklenecek.
            </EmptyDescription>
          </EmptyState>
        )}
      </Content>
    </Container>
  );
};

export default Profile;
