import React, { useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import { useTheme } from '../contexts/ThemeContext';
import { 
  FiGrid,
  FiHeart,
  FiMessageCircle,
  FiMoreHorizontal,
  FiBookmark,
  FiUserPlus,
  FiUserMinus,
  FiEdit3,
  FiPlus
} from 'react-icons/fi';
import { useAuth } from '../contexts/AuthContext';

const Container = styled.div`
  min-height: 100vh;
  background: ${props => props.theme.background};
  padding-top: 80px;
`;

const ProfileContainer = styled.div`
  max-width: 935px;
  margin: 0 auto;
  padding: 30px 20px;
`;

const ProfileHeader = styled.div`
  display: flex;
  gap: 30px;
  margin-bottom: 44px;
  padding-bottom: 30px;
  border-bottom: 1px solid ${props => props.theme.border};
  align-items: flex-start;

  @media (max-width: 768px) {
    flex-direction: column;
    align-items: center;
    text-align: center;
    gap: 20px;
  }
`;

const AvatarSection = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  flex-shrink: 0;
`;

const Avatar = styled.div`
  width: 150px;
  height: 150px;
  border-radius: 50%;
  background: ${props => props.theme.gradient};
  display: flex;
  align-items: center;
  justify-content: center;
  color: white;
  font-size: 3rem;
  font-weight: 700;
  border: 3px solid ${props => props.theme.border};
  flex-shrink: 0;

  @media (max-width: 768px) {
    width: 120px;
    height: 120px;
    font-size: 2.5rem;
  }
`;

const AvatarImg = styled.img`
  width: 150px;
  height: 150px;
  border-radius: 50%;
  object-fit: cover;
  border: 3px solid ${props => props.theme.border};
  flex-shrink: 0;

  @media (max-width: 768px) {
    width: 120px;
    height: 120px;
  }
`;

const ProfileInfo = styled.div`
  flex: 1;
  min-width: 0;
`;

const ProfileTop = styled.div`
  display: flex;
  align-items: center;
  gap: 20px;
  margin-bottom: 20px;
  flex-wrap: wrap;

  @media (max-width: 768px) {
    flex-direction: column;
    align-items: center;
    gap: 12px;
  }
`;

const Username = styled.h1`
  font-size: 1.75rem;
  font-weight: 300;
  color: ${props => props.theme.text};
  margin: 0;
  line-height: 1.2;
  flex-shrink: 0;

  @media (max-width: 768px) {
    font-size: 1.5rem;
  }
`;

const ActionButtons = styled.div`
  display: flex;
  gap: 8px;
  align-items: center;
  flex-wrap: wrap;
  flex-shrink: 0;
`;

const ActionButton = styled.button`
  padding: 8px 16px;
  border-radius: 8px;
  border: 1px solid ${props => props.theme.border};
  background: ${props => props.primary ? props.theme.primary : props.theme.surface};
  color: ${props => props.primary ? 'white' : props.theme.text};
  font-weight: 600;
  font-size: 0.9rem;
  cursor: pointer;
  transition: all 0.3s ease;
  display: flex;
  align-items: center;
  gap: 4px;
  white-space: nowrap;

  &:hover {
    background: ${props => props.primary ? props.theme.primaryHover : props.theme.surfaceHover};
  }
`;

const MoreButton = styled.button`
  padding: 8px;
  border-radius: 8px;
  border: 1px solid ${props => props.theme.border};
  background: ${props => props.theme.surface};
  color: ${props => props.theme.text};
  cursor: pointer;
  transition: all 0.3s ease;
  display: flex;
  align-items: center;
  justify-content: center;

  &:hover {
    background: ${props => props.theme.surfaceHover};
  }
`;

const Stats = styled.div`
  display: flex;
  gap: 40px;
  margin-bottom: 20px;

  @media (max-width: 768px) {
    justify-content: center;
    gap: 30px;
  }
`;

const StatItem = styled.div`
  text-align: left;

  @media (max-width: 768px) {
    text-align: center;
  }
`;

const StatNumber = styled.div`
  font-size: 1.1rem;
  font-weight: 600;
  color: ${props => props.theme.text};
  margin-bottom: 2px;
`;

const StatLabel = styled.div`
  font-size: 0.9rem;
  color: ${props => props.theme.textSecondary};
`;

const Bio = styled.div`
  color: ${props => props.theme.text};
  line-height: 1.6;
  margin-bottom: 16px;
  font-size: 0.95rem;
`;

const BioText = styled.span`
  margin: 0;
  font-size: 0.95rem;
  display: inline;
  white-space: normal;
  line-height: 1.6;
`;

const BioLink = styled.a`
  color: ${props => props.theme.primary};
  text-decoration: none;
  font-weight: 500;

  &:hover {
    text-decoration: underline;
  }
`;

const ProfileTabs = styled.div`
  display: flex;
  justify-content: center;
  border-top: 1px solid ${props => props.theme.border};
  margin-top: 0;
`;

const Tab = styled.button`
  display: flex;
  align-items: center;
  gap: 8px;
  padding: 16px 0;
  background: none;
  border: none;
  color: ${props => props.active ? props.theme.text : props.theme.textSecondary};
  font-weight: 600;
  font-size: 0.9rem;
  cursor: pointer;
  border-top: 1px solid ${props => props.active ? props.theme.text : 'transparent'};
  transition: all 0.3s ease;
  margin-right: 60px;
  text-transform: uppercase;
  letter-spacing: 0.5px;

  &:last-child {
    margin-right: 0;
  }

  &:hover {
    color: ${props => props.theme.text};
  }
`;

const WorksGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 28px;
  margin-top: 0;

  @media (max-width: 768px) {
    gap: 3px;
  }
`;

const WorkItem = styled.div`
  aspect-ratio: 1;
  background: ${props => props.theme.surface};
  border: 1px solid ${props => props.theme.border};
  position: relative;
  cursor: pointer;
  overflow: hidden;
  transition: all 0.3s ease;
  border-radius: 8px;

  &:hover {
    transform: scale(1.02);
    z-index: 10;
    box-shadow: 0 8px 25px ${props => props.theme.shadow};
  }
`;

const WorkImage = styled.img`
  width: 100%;
  height: 100%;
  object-fit: cover;
  transition: all 0.3s ease;

  ${WorkItem}:hover & {
    transform: scale(1.1);
  }
`;

const WorkOverlay = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: rgba(0, 0, 0, 0.5);
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 20px;
