const Profile = () => {
  const { username } = useParams();
  const navigate = useNavigate();
  const { theme } = useTheme();
  const { user: currentUser } = useAuth();
  const [activeTab, setActiveTab] = useState('posts');
  const [isFollowing, setIsFollowing] = useState(mockProfile.isFollowing);

  const profile = mockProfile;
  const works = mockWorks;
  const isOwnProfile = currentUser && currentUser.username === username;

  const handleFollow = () => {
    setIsFollowing(!isFollowing);
  };

  const handleWorkClick = (workId) => {
    navigate(`/work/${workId}`);
  };

  return (
    <Container theme={theme}>
      <ProfileContainer theme={theme}>
        <ProfileHeader theme={theme}>
          <AvatarSection>
            {profile.avatar ? (
              <AvatarImg src={profile.avatar} alt={profile.fullName} theme={theme} />
            ) : (
              <Avatar theme={theme}>
                {profile.fullName.charAt(0).toUpperCase()}
              </Avatar>
            )}
          </AvatarSection>

          <ProfileInfo>
            <ProfileTop>
              <Username theme={theme}>{profile.username}</Username>
              <ActionButtons>
                {isOwnProfile ? (
                  <>
                    <ActionButton theme={theme} onClick={() => navigate('/settings')}>
                      <FiEdit3 size={14} />
                      Profili Düzenle
                    </ActionButton>
                    <ActionButton theme={theme} primary>
                      <FiPlus size={14} />
                      Eser Ekle
                    </ActionButton>
                    <MoreButton theme={theme}>
                      <FiMoreHorizontal size={16} />
                    </MoreButton>
                  </>
                ) : (
                  <>
                    <ActionButton 
                      theme={theme} 
                      primary={!isFollowing}
                      onClick={handleFollow}
                    >
                      {isFollowing ? (
                        <>
                          <FiUserMinus size={14} />
                          Takibi Bırak
                        </>
                      ) : (
                        <>
                          <FiUserPlus size={14} />
                          Takip Et
                        </>
                      )}
                    </ActionButton>
                    <ActionButton theme={theme}>
                      <FiMessageCircle size={14} />
                      Mesaj
                    </ActionButton>
                    <MoreButton theme={theme}>
                      <FiMoreHorizontal size={16} />
                    </MoreButton>
                  </>
                )}
              </ActionButtons>
            </ProfileTop>

            <Stats>
              <StatItem>
                <StatNumber theme={theme}>{profile.posts}</StatNumber>
                <StatLabel theme={theme}>eser</StatLabel>
              </StatItem>
              <StatItem>
                <StatNumber theme={theme}>{profile.followers.toLocaleString()}</StatNumber>
                <StatLabel theme={theme}>takipçi</StatLabel>
              </StatItem>
              <StatItem>
                <StatNumber theme={theme}>{profile.following}</StatNumber>
                <StatLabel theme={theme}>takip</StatLabel>
              </StatItem>
            </Stats>

            <Bio theme={theme}>
              <BioText theme={theme}>
                {profile.fullName}
                {profile.bio && (
                  <>
                    <br />
                    {profile.bio.split('\n').map((line, index) => (
                      <span key={index}>
                        {line}
                        {index < profile.bio.split('\n').length - 1 && <br />}
                      </span>
                    ))}
                  </>
                )}
                {profile.website && (
                  <>
                    <br />
                    <BioLink theme={theme} href={profile.website} target="_blank" rel="noopener noreferrer">
                      {profile.website}
                    </BioLink>
                  </>
                )}
              </BioText>
            </Bio>
          </ProfileInfo>
        </ProfileHeader>

        <ProfileTabs theme={theme}>
          <Tab 
            theme={theme} 
            active={activeTab === 'posts'}
            onClick={() => setActiveTab('posts')}
          >
            <FiGrid size={12} />
            Eserler
          </Tab>
          <Tab 
            theme={theme} 
            active={activeTab === 'saved'}
            onClick={() => setActiveTab('saved')}
          >
            <FiBookmark size={12} />
            Kaydedilenler
          </Tab>
        </ProfileTabs>

        {activeTab === 'posts' && (
          <>
            {works.length > 0 ? (
              <WorksGrid>
                {works.map((work) => (
                  <WorkItem 
                    key={work.id} 
                    theme={theme}
                    onClick={() => handleWorkClick(work.id)}
                  >
                    <WorkImage src={work.image} alt={`Eser ${work.id}`} />
                    <WorkOverlay>
                      <OverlayIcon>
                        <FiHeart size={16} />
                        {work.likes}
                      </OverlayIcon>
                      <OverlayIcon>
                        <FiMessageCircle size={16} />
                        {work.comments}
                      </OverlayIcon>
                    </WorkOverlay>
                  </WorkItem>
                ))}
              </WorksGrid>
            ) : (
              <EmptyState theme={theme}>
                <EmptyIcon>📷</EmptyIcon>
                <EmptyTitle theme={theme}>Henüz eser yok</EmptyTitle>
                <EmptyDescription theme={theme}>
                  {isOwnProfile 
                    ? 'İlk eserinizi paylaşmaya ne dersiniz?'
                    : 'Bu kullanıcı henüz eser paylaşmamış.'
                  }
                </EmptyDescription>
              </EmptyState>
            )}
          </>
        )}

        {activeTab === 'saved' && (
          <EmptyState theme={theme}>
            <EmptyIcon>🔖</EmptyIcon>
            <EmptyTitle theme={theme}>Kaydedilen eserler</EmptyTitle>
            <EmptyDescription theme={theme}>
              Kaydettiğiniz eserler burada görünecek.
            </EmptyDescription>
          </EmptyState>
        )}
      </ProfileContainer>
    </Container>
  );
};

export default Profile;
