// Mock data for Instagram-like profile
const mockProfile = {
  username: 'zeynep_esmer',
  fullName: 'Zeynep Esmer',
  bio: '🎨 Sanatçı & Tasarımcı\n📍 İstanbul, Türkiye\n✨ Dijital sanat ve geleneksel tekniklerin buluştuğu nokta',
  website: 'zeynepesmer.com',
  location: 'İstanbul, Türkiye',
  avatar: '/zeynep.jpg',
  followers: 2847,
  following: 156,
  posts: 42,
  isFollowing: false,
  isOwnProfile: true
};

const mockWorks = [
  { id: 1, image: '/t1.jpg', likes: 234, comments: 12 },
  { id: 2, image: '/t2.webp', likes: 189, comments: 8 },
  { id: 3, image: '/t3.jpg', likes: 156, comments: 5 },
  { id: 4, image: '/t4.jpg', likes: 298, comments: 18 },
  { id: 5, image: '/t6.jpg', likes: 167, comments: 9 },
  { id: 6, image: '/t7.webp', likes: 223, comments: 14 },
  { id: 7, image: '/t8.jpg', likes: 145, comments: 7 },
  { id: 8, image: '/t10.jpg', likes: 189, comments: 11 },
  { id: 9, image: '/t11.jpeg', likes: 134, comments: 6 },
  { id: 10, image: '/t12.jpeg', likes: 267, comments: 16 },
  { id: 11, image: '/leo1.jpg', likes: 312, comments: 22 },
  { id: 12, image: '/leo2.jpeg', likes: 198, comments: 13 },
  { id: 13, image: '/picasso.webp', likes: 245, comments: 19 },
  { id: 14, image: '/can.jpg', likes: 178, comments: 8 },
  { id: 15, image: '/sude.jpg', likes: 156, comments: 4 }
];

const Profile = () => {
  const { username } = useParams();
  const navigate = useNavigate();
  const location = useLocation();
  const { theme } = useTheme();
  const { user: currentUser } = useAuth();
  const [activeTab, setActiveTab] = useState('posts');
  const [isFollowing, setIsFollowing] = useState(mockProfile.isFollowing);
  const [showPhotoUpload, setShowPhotoUpload] = useState(false);
  const [selectedFile, setSelectedFile] = useState(null);
  const [previewUrl, setPreviewUrl] = useState(null);
  const fileInputRef = useRef(null);

  const profile = mockProfile;
  const works = mockWorks;
  const isOwnProfile = mockProfile.isOwnProfile || (currentUser && currentUser.username === username);

  const handleFollow = () => {
    setIsFollowing(!isFollowing);
  };

  const handleWorkClick = (workId) => {
    navigate(`/work/${workId}`);
  };

  const handlePhotoUpload = () => {
    setShowPhotoUpload(true);
  };

  const handleFileSelect = (event) => {
    const file = event.target.files[0];
    if (file) {
      setSelectedFile(file);
      const reader = new FileReader();
      reader.onload = (e) => {
        setPreviewUrl(e.target.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSavePhoto = () => {
    if (selectedFile) {
      // Burada gerçek API çağrısı yapılacak
      console.log('Fotoğraf kaydediliyor:', selectedFile);
      // Şimdilik mock olarak profil fotoğrafını güncelle
      mockProfile.avatar = previewUrl;
      setShowPhotoUpload(false);
      setSelectedFile(null);
      setPreviewUrl(null);
    }
  };

  const handleCancelPhoto = () => {
    setShowPhotoUpload(false);
    setSelectedFile(null);
    setPreviewUrl(null);
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
  };

  return (
    <Container theme={theme}>
      <LeftSidebar theme={theme}>
        <SidebarMenu>
          <MenuItem theme={theme} active={location.pathname === '/'} onClick={() => navigate('/')}>
            <MenuIcon>
              <FiHome />
            </MenuIcon>
            <MenuText>Ana Sayfa</MenuText>
          </MenuItem>

          <MenuItem theme={theme} active={location.pathname === '/explore'} onClick={() => navigate('/explore')}>
            <MenuIcon>
              <FiEye />
            </MenuIcon>
            <MenuText>Keşfet</MenuText>
          </MenuItem>

          <MenuItem theme={theme} active={location.pathname === '/notifications'} onClick={() => navigate('/notifications')}>
            <MenuIcon>
              <FiBell />
            </MenuIcon>
            <MenuText>Bildirimler</MenuText>
          </MenuItem>

          <MenuItem theme={theme} active={location.pathname.startsWith('/profile')} onClick={() => navigate('/profile')}>
            <MenuIcon>
              <FiUser />
            </MenuIcon>
            <MenuText>Profil</MenuText>
          </MenuItem>

          <MenuItem theme={theme} active={location.pathname === '/saved'} onClick={() => navigate('/saved')}>
            <MenuIcon>
              <FiBookmark />
            </MenuIcon>
            <MenuText>Kaydedilenler</MenuText>
          </MenuItem>
        </SidebarMenu>
      </LeftSidebar>

      <MainLayout>
        <Content theme={theme}>
          <ContentInner>
            <ProfileContainer theme={theme}>
        <ProfileHeader theme={theme}>
          <AvatarSection>
            {profile.avatar ? (
              <AvatarImg src={profile.avatar} alt={profile.fullName} theme={theme} />
            ) : (
              <Avatar theme={theme}>
                {profile.fullName.charAt(0).toUpperCase()}
              </Avatar>
            )}
            {isOwnProfile && (
              <PhotoUploadButton theme={theme} onClick={handlePhotoUpload}>
                <FiCamera size={18} />
              </PhotoUploadButton>
            )}
          </AvatarSection>

          <ProfileInfo>
            <ProfileTop>
              <Username theme={theme}>{profile.username}</Username>
            </ProfileTop>

            <Stats>
              <StatItem>
                <StatNumber theme={theme}>{profile.posts}</StatNumber>
                <StatLabel theme={theme}>eser</StatLabel>
              </StatItem>
              <StatItem>
                <StatNumber theme={theme}>{profile.followers.toLocaleString()}</StatNumber>
                <StatLabel theme={theme}>takipçi</StatLabel>
              </StatItem>
              <StatItem>
                <StatNumber theme={theme}>{profile.following}</StatNumber>
                <StatLabel theme={theme}>takip</StatLabel>
              </StatItem>
            </Stats>

            <Bio theme={theme}>
              <BioText theme={theme}>
                {profile.fullName}
                {profile.bio && (
                  <>
                    <br />
                    {profile.bio.split('\n').map((line, index) => (
                      <span key={index}>
                        {line}
                        {index < profile.bio.split('\n').length - 1 && <br />}
                      </span>
                    ))}
                  </>
                )}
                {profile.website && (
                  <>
                    <br />
                    <BioLink theme={theme} href={profile.website} target="_blank" rel="noopener noreferrer">
                      {profile.website}
                    </BioLink>
                  </>
                )}
              </BioText>
            </Bio>

            <ActionButtons>
              {isOwnProfile ? (
                <>
                  <ActionButton theme={theme} onClick={() => navigate('/edit-profile')}>
                    <FiEdit3 size={14} />
                    Profili Düzenle
                  </ActionButton>
                  <ActionButton theme={theme} primary>
                    <FiPlus size={14} />
                    Eser Ekle
                  </ActionButton>
                </>
              ) : (
                <>
                  <ActionButton 
                    theme={theme} 
                    primary={!isFollowing}
                    onClick={handleFollow}
                  >
                    {isFollowing ? (
                      <>
                        <FiUserMinus size={14} />
                        Takibi Bırak
                      </>
                    ) : (
                      <>
                        <FiUserPlus size={14} />
                        Takip Et
                      </>
                    )}
                  </ActionButton>
                  <ActionButton theme={theme}>
                    <FiMessageCircle size={14} />
                    Mesaj
                  </ActionButton>
                </>
              )}
            </ActionButtons>
          </ProfileInfo>
        </ProfileHeader>

        <ProfileTabs theme={theme}>
          <Tab 
            theme={theme} 
            active={activeTab === 'posts'}
            onClick={() => setActiveTab('posts')}
          >
            <FiGrid size={12} />
            Eserler
          </Tab>
          <Tab 
            theme={theme} 
            active={activeTab === 'saved'}
            onClick={() => setActiveTab('saved')}
          >
            <FiBookmark size={12} />
            Kaydedilenler
          </Tab>
        </ProfileTabs>

        {activeTab === 'posts' && (
          <>
            {works.length > 0 ? (
              <WorksGrid>
                {works.map((work) => (
                  <WorkItem 
                    key={work.id} 
                    theme={theme}
                    onClick={() => handleWorkClick(work.id)}
                  >
                    <WorkImage src={work.image} alt={`Eser ${work.id}`} />
                    <WorkOverlay>
                      <OverlayIcon>
                        <FiHeart size={16} />
                        {work.likes}
                      </OverlayIcon>
                      <OverlayIcon>
                        <FiMessageCircle size={16} />
                        {work.comments}
                      </OverlayIcon>
                    </WorkOverlay>
                  </WorkItem>
                ))}
              </WorksGrid>
            ) : (
              <EmptyState theme={theme}>
                <EmptyIcon>📷</EmptyIcon>
                <EmptyTitle theme={theme}>Henüz eser yok</EmptyTitle>
                <EmptyDescription theme={theme}>
                  {isOwnProfile 
                    ? 'İlk eserinizi paylaşmaya ne dersiniz?'
                    : 'Bu kullanıcı henüz eser paylaşmamış.'
                  }
                </EmptyDescription>
              </EmptyState>
            )}
          </>
        )}

        {activeTab === 'saved' && (
          <EmptyState theme={theme}>
            <EmptyIcon>🔖</EmptyIcon>
            <EmptyTitle theme={theme}>Kaydedilen eserler</EmptyTitle>
            <EmptyDescription theme={theme}>
              Kaydettiğiniz eserler burada görünecek.
            </EmptyDescription>
          </EmptyState>
        )}
            </ProfileContainer>
          </ContentInner>
        </Content>
      </MainLayout>

      {/* Fotoğraf Yükleme Modalı */}
      {showPhotoUpload && (
        <PhotoUploadModal onClick={handleCancelPhoto}>
          <PhotoUploadContent theme={theme} onClick={(e) => e.stopPropagation()}>
            <PhotoUploadTitle theme={theme}>Profil Fotoğrafı Değiştir</PhotoUploadTitle>

            <PhotoPreview theme={theme}>
              {previewUrl ? (
                <PhotoPreviewImg src={previewUrl} alt="Önizleme" />
              ) : (
                <div style={{ color: theme.text, fontSize: '3rem' }}>
                  <FiCamera />
                </div>
              )}
            </PhotoPreview>

            <HiddenFileInput
              ref={fileInputRef}
              type="file"
              accept="image/*"
              onChange={handleFileSelect}
            />

            <PhotoUploadActions>
              <PhotoUploadButton2 
                theme={theme} 
                onClick={() => fileInputRef.current?.click()}
              >
                Fotoğraf Seç
              </PhotoUploadButton2>

              {previewUrl && (
                <PhotoUploadButton2 
                  theme={theme} 
                  primary 
                  onClick={handleSavePhoto}
                >
                  Kaydet
                </PhotoUploadButton2>
              )}

              <PhotoUploadButton2 
                theme={theme} 
                onClick={handleCancelPhoto}
              >
                İptal
              </PhotoUploadButton2>
            </PhotoUploadActions>
          </PhotoUploadContent>
        </PhotoUploadModal>
      )}
    </Container>
  );
};

export default Profile;
