import React, { useState } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import styled from 'styled-components';
import { motion, AnimatePresence } from 'framer-motion';
import { useAuth } from '../contexts/AuthContext';
import { useTheme } from '../contexts/ThemeContext';
import { 
  FiHome, 
  FiSearch, 
  FiUser, 
  FiBookmark, 
  FiSun, 
  FiMoon, 
  FiMenu, 
  FiX,
  FiLogOut,
  FiSettings,
  FiVolume2,
  FiVolumeX
} from 'react-icons/fi';

const Nav = styled.nav`
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  z-index: 1000;
  background: ${props => props.theme.glass};
  backdrop-filter: blur(20px);
  border-bottom: 1px solid ${props => props.theme.glassBorder};
  transition: all 0.3s ease;
  width: 100%;
  box-sizing: border-box;
`;

const NavContainer = styled.div`
  max-width: 100%;
  margin: 0;
  padding: 0 20px 0 20px;
  display: flex;
  align-items: center;
  justify-content: space-between;
  height: 80px;
  width: 100%;
  box-sizing: border-box;
`;

const Logo = styled(Link)`
  font-size: 24px;
  font-weight: 800;
  color: ${props => props.theme.primary};
  text-decoration: none;
  display: flex;
  align-items: center;
  gap: 8px;

  &:hover {
    color: ${props => props.theme.primaryHover};
  }
`;

const LogoIcon = styled.div`
  width: 120px;
  height: 40px;
  display: flex;
  align-items: center;
  justify-content: center;

  img {
    width: 100%;
    height: 100%;
    object-fit: contain;
  }
`;

// const NavLinks = styled.div`
//   display: flex;
//   align-items: center;
//   gap: 32px;

//   @media (max-width: 768px) {
//     display: none;
//   }
// `;

// const NavLink = styled(Link)`
//   color: ${props => props.theme.text};
//   text-decoration: none;
//   font-weight: 500;
//   padding: 8px 16px;
//   border-radius: 8px;
//   transition: all 0.2s ease;
//   display: flex;
//   align-items: center;
//   gap: 8px;

//   &:hover {
//     background: ${props => props.theme.surfaceHover};
//     color: ${props => props.theme.primary};
//   }

//   &.active {
//     background: ${props => props.theme.primaryLight};
//     color: ${props => props.theme.primary};
//   }
// `;

const NavActions = styled.div`
  display: flex;
  align-items: center;
  gap: 12px;
  margin-left: auto;
  margin-right: 20px;
  padding-right: 0;
`;

const LeftNavActions = styled.div`
  display: flex;
  align-items: center;
  gap: 16px;
  margin-left: 0;
  margin-right: auto;
  padding-right: 0;
`;


const MobileMenu = styled(motion.div)`
  position: fixed;
  top: 0;
  left: 0;
  width: 280px;
  height: 100vh;
  background: #000000;
  border-right: 1px solid #262626;
  z-index: 9999;
  padding: 20px 0;
  overflow-y: auto;
  box-shadow: 2px 0 10px rgba(0, 0, 0, 0.5);
`;

const MenuHeader = styled.div`
  padding: 0 24px 20px 24px;
  border-bottom: 1px solid #262626;
  margin-bottom: 20px;
`;

const MenuTitle = styled.h3`
  font-size: 20px;
  font-weight: 600;
  color: #ffffff;
  margin: 0 0 4px 0;
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
`;

const MenuSubtitle = styled.p`
  font-size: 13px;
  color: #8e8e8e;
  margin: 0;
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
`;

const MenuItems = styled.div`
  padding: 0 12px;
`;

const MenuItem = styled(Link)`
  display: flex;
  align-items: center;
  gap: 16px;
  padding: 12px 12px;
  margin-bottom: 4px;
  border-radius: 8px;
  text-decoration: none;
  color: #ffffff;
  transition: all 0.2s ease;
  font-weight: 400;
  font-size: 16px;
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;

  &:hover {
    background: #262626;
    color: #ffffff;
  }

  &.active {
    background: #262626;
    color: #ffffff;
    font-weight: 600;
  }
`;

const MenuIcon = styled.div`
  font-size: 24px;
  display: flex;
  align-items: center;
  justify-content: center;
`;

const MenuText = styled.span`
  font-size: 16px;
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
`;

const MenuFooter = styled.div`
  padding: 20px 24px;
  border-top: 1px solid #262626;
  margin-top: 20px;
`;

const LogoutButton = styled.button`
  display: flex;
  align-items: center;
  gap: 12px;
  width: 100%;
  padding: 12px 16px;
  border: none;
  border-radius: 8px;
  background: transparent;
  color: ${props => props.theme.text};
  cursor: pointer;
  transition: all 0.3s ease;
  font-size: 16px;
  font-weight: 500;

  &:hover {
    background: ${props => props.theme.dangerLight};
    color: ${props => props.theme.danger};
  }
`;

const Overlay = styled(motion.div)`
  position: fixed;
  top: 0;
  left: 0;
  width: 100vw;
  height: 100vh;
  background: rgba(0, 0, 0, 0.5);
  z-index: 9998;
`;

const SearchContainer = styled.div`
  position: relative;
  flex: 1;
  max-width: 500px;
  margin: 0 auto;
  display: flex;
  align-items: center;
`;

const SearchInput = styled.input`
  width: 100%;
  height: 40px;
  padding: 0 16px 0 44px;
  border: 1px solid ${props => props.theme.glassBorder};
  border-radius: 20px;
  background: ${props => props.theme.glass};
  backdrop-filter: blur(20px);
  color: ${props => props.theme.text};
  font-size: 14px;
  transition: all 0.3s ease;
  cursor: pointer;

  &::placeholder {
    color: ${props => props.theme.textSecondary};
  }

  &:focus {
    outline: none;
    border-color: ${props => props.theme.primary};
    box-shadow: 0 0 0 3px ${props => props.theme.primaryLight};
  }

  &:hover {
    border-color: ${props => props.theme.primary};
  }
`;

const SearchIcon = styled.div`
  position: absolute;
  left: 14px;
  top: 50%;
  transform: translateY(-50%);
  color: ${props => props.theme.textSecondary};
  pointer-events: none;
`;

const ThemeToggle = styled.button`
  width: 40px;
  height: 40px;
  border-radius: 50%;
  background: ${props => props.theme.surface};
  border: 1px solid ${props => props.theme.border};
  color: ${props => props.theme.text};
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  transition: all 0.2s ease;

  &:hover {
    background: ${props => props.theme.surfaceHover};
    transform: scale(1.05);
  }
`;

const MusicToggle = styled.button`
  width: 40px;
  height: 40px;
  border-radius: 50%;
  background: ${props => props.theme.surface};
  border: 1px solid ${props => props.theme.border};
  color: ${props => props.isPlaying ? props.theme.primary : props.theme.text};
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  transition: all 0.2s ease;
  position: relative;

  &:hover {
    background: ${props => props.theme.surfaceHover};
    transform: scale(1.05);
  }

  &::after {
    content: "${props => props.isPlaying ? 'MÃ¼zik Ã‡alÄ±yor' : 'MÃ¼zik Ã‡al'}";
    position: absolute;
    bottom: -30px;
    left: 50%;
    transform: translateX(-50%);
    background: ${props => props.theme.surface};
    color: ${props => props.theme.text};
    padding: 4px 8px;
    border-radius: 4px;
    font-size: 12px;
    white-space: nowrap;
    opacity: 0;
    pointer-events: none;
    transition: opacity 0.2s ease;
    z-index: 1000;
  }

  &:hover::after {
    opacity: 1;
  }
`;

const UserMenu = styled.div`
  position: relative;
`;

const UserButton = styled.button`
  width: 40px;
  height: 40px;
  border-radius: 50%;
  background: ${props => props.theme.gradient};
  border: none;
  color: white;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  font-weight: 600;
  transition: all 0.2s ease;

  &:hover {
    transform: scale(1.05);
  }
`;

const UserAvatar = styled.img`
  width: 100%;
  height: 100%;
  border-radius: 50%;
  object-fit: cover;
`;

const Dropdown = styled(motion.div)`
  position: absolute;
  top: 100%;
  right: 0;
  margin-top: 8px;
  background: ${props => props.theme.surface};
  border: 1px solid ${props => props.theme.border};
  border-radius: 12px;
  box-shadow: 0 10px 40px ${props => props.theme.shadow};
  min-width: 200px;
  overflow: hidden;
`;

const DropdownItem = styled.button`
  width: 100%;
  padding: 12px 16px;
  background: none;
  border: none;
  color: ${props => props.theme.text};
  text-align: left;
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: 12px;
  transition: all 0.2s ease;

  &:hover {
    background: ${props => props.theme.surfaceHover};
  }
`;

const AuthButtons = styled.div`
  display: flex;
  align-items: center;
  gap: 12px;
`;

const AuthButton = styled(Link)`
  padding: 8px 16px;
  border-radius: 8px;
  text-decoration: none;
  font-weight: 500;
  transition: all 0.2s ease;
  display: flex;
  align-items: center;
  gap: 8px;

  &.primary {
    background: ${props => props.theme.primary};
    color: white;

    &:hover {
      background: ${props => props.theme.primaryHover};
      transform: translateY(-1px);
    }
  }

  &.secondary {
    background: ${props => props.theme.surface};
    color: ${props => props.theme.text};
    border: 1px solid ${props => props.theme.border};

    &:hover {
      background: ${props => props.theme.surfaceHover};
    }
  }
`;

const MobileMenuButton = styled.button`
  display: none;
  width: 40px;
  height: 40px;
  border-radius: 8px;
  background: ${props => props.theme.surface};
  border: 1px solid ${props => props.theme.border};
  color: ${props => props.theme.text};
  cursor: pointer;
  transition: all 0.2s ease;

  @media (max-width: 768px) {
    display: flex;
    align-items: center;
    justify-content: center;
  }

  &:hover {
    background: ${props => props.theme.surfaceHover};
  }
`;


const MobileNavLink = styled(Link)`
  display: flex;
  align-items: center;
  gap: 12px;
  padding: 12px 0;
  color: ${props => props.theme.text};
  text-decoration: none;
  font-weight: 500;
  border-bottom: 1px solid ${props => props.theme.border};
  transition: color 0.3s ease, background-color 0.3s ease;

  &:last-child {
    border-bottom: none;
  }

  &:hover {
    color: ${props => props.theme.primary};
  }
`;

const Navbar = () => {
  const { user, logout } = useAuth();
  const { isDark, toggleTheme } = useTheme();
  const navigate = useNavigate();
  const location = useLocation();
  const [showUserMenu, setShowUserMenu] = useState(false);
  const [showMobileMenu, setShowMobileMenu] = useState(false);
  const [isMusicPlaying, setIsMusicPlaying] = useState(false);
  const [currentTrack] = useState(0);
  const [audio, setAudio] = useState(null);

  // Sanatsal mÃ¼zik dosyalarÄ± - Ãœcretsiz jazz mÃ¼zik kaynaklarÄ±
  const musicTracks = [
    {
      name: "Jazz MÃ¼zik",
      url: "/music/jazz1.mp3", // Yerel jazz mÃ¼zik dosyasÄ±
      artist: "Feellink Jazz Collection"
    }
  ];

  const toggleMusic = () => {
    if (isMusicPlaying) {
      // MÃ¼ziÄŸi durdur
      if (audio) {
        audio.pause();
        audio.currentTime = 0;
      }
      setIsMusicPlaying(false);
    } else {
      // MÃ¼ziÄŸi baÅŸlat
      playMusic();
    }
  };

  const nextTrack = () => {
    // Tek mÃ¼zik olduÄŸu iÃ§in aynÄ± mÃ¼ziÄŸi tekrar baÅŸlat
    if (isMusicPlaying) {
      playMusic();
    }
  };

  // GerÃ§ek mÃ¼zik Ã§alma fonksiyonu
  const playMusic = () => {
    // Ã–nceki mÃ¼ziÄŸi durdur
    if (audio) {
      audio.pause();
      audio.currentTime = 0;
    }

    // Yeni mÃ¼zik oluÅŸtur
    const newAudio = new Audio(musicTracks[currentTrack].url);

    // MÃ¼zik yÃ¼klendiÄŸinde Ã§al
    newAudio.addEventListener('canplaythrough', () => {
      newAudio.play().catch(error => {
        console.log('MÃ¼zik Ã§alma hatasÄ±:', error);
        // EÄŸer mÃ¼zik Ã§alÄ±namazsa, simÃ¼lasyon gÃ¶ster
        console.log(`ðŸŽµ Åžu anda Ã§alÄ±yor: ${musicTracks[currentTrack].name}`);
      });
    });

    // MÃ¼zik bittiÄŸinde tekrar baÅŸlat
    newAudio.addEventListener('ended', () => {
      if (isMusicPlaying) {
        playMusic(); // AynÄ± mÃ¼ziÄŸi tekrar baÅŸlat
      }
    });

    // Hata durumunda simÃ¼lasyon gÃ¶ster
    newAudio.addEventListener('error', () => {
      console.log(`ðŸŽµ Åžu anda Ã§alÄ±yor: ${musicTracks[currentTrack].name}`);
    });

    setAudio(newAudio);
    setIsMusicPlaying(true);
  };

  const handleLogout = () => {
    logout();
    setShowUserMenu(false);
    // navigate('/') removed - AuthContext handles redirect to /login
  };


  const isActive = (path) => {
    return location.pathname === path;
  };

  return (
    <Nav>
      <NavContainer>
        <LeftNavActions>
          <Logo to="/">
            <LogoIcon>
              <img src="/images/feellink.logo.png" alt="Feellink Logo" />
            </LogoIcon>
          </Logo>
        </LeftNavActions>

        {user && (
          <SearchContainer>
            <SearchInput
              type="text"
              placeholder="Eserler, sanatÃ§Ä±lar, kategoriler ara..."
              onClick={() => navigate('/explore')}
            />
            <SearchIcon>
              <FiSearch size={18} />
            </SearchIcon>
          </SearchContainer>
        )}

        <NavActions>
          <MusicToggle onClick={toggleMusic} isPlaying={isMusicPlaying}>
            {isMusicPlaying ? <FiVolume2 size={20} /> : <FiVolumeX size={20} />}
          </MusicToggle>

          <ThemeToggle onClick={toggleTheme}>
            {isDark ? <FiSun size={20} /> : <FiMoon size={20} />}
          </ThemeToggle>

          {user ? (
            <UserMenu>
              <UserButton onClick={() => setShowUserMenu(!showUserMenu)}>
                {user.avatar ? (
                  <UserAvatar src={user.avatar} alt={user.fullName} />
                ) : (
                  user.fullName.charAt(0).toUpperCase()
                )}
              </UserButton>

              <AnimatePresence>
                {showUserMenu && (
                  <Dropdown
                    initial={{ opacity: 0, y: -10 }}
                    animate={{ opacity: 1, y: 0 }}
                    exit={{ opacity: 0, y: -10 }}
                    transition={{ duration: 0.2 }}
                  >
                    <DropdownItem onClick={() => navigate(`/profile/${user.username}`)}>
                      <FiUser size={14} />
                      Profilim
                    </DropdownItem>
                    <DropdownItem onClick={() => navigate('/saved')}>
                      <FiBookmark size={18} />
                      Kaydedilenler
                    </DropdownItem>
                    <DropdownItem onClick={() => navigate('/settings')}>
                      <FiSettings size={18} />
                      Ayarlar
                    </DropdownItem>
                    <DropdownItem onClick={handleLogout}>
                      <FiLogOut size={18} />
                      Ã‡Ä±kÄ±ÅŸ Yap
                    </DropdownItem>
                  </Dropdown>
                )}
              </AnimatePresence>
            </UserMenu>
          ) : (
            <AuthButtons>
              <AuthButton to="/login" className="secondary">
                GiriÅŸ Yap
              </AuthButton>
              <AuthButton to="/register" className="primary">
                KayÄ±t Ol
              </AuthButton>
            </AuthButtons>
          )}

          <MobileMenuButton onClick={() => setShowMobileMenu(!showMobileMenu)}>
            {showMobileMenu ? <FiX size={20} /> : <FiMenu size={20} />}
          </MobileMenuButton>
        </NavActions>
      </NavContainer>

      <AnimatePresence>
        {showMobileMenu && (
          <MobileMenu
            initial={{ opacity: 0, height: 0 }}
            animate={{ opacity: 1, height: 'auto' }}
            exit={{ opacity: 0, height: 0 }}
            transition={{ duration: 0.3 }}
          >
            <MobileNavLink to="/" onClick={() => setShowMobileMenu(false)}>
              <FiHome size={18} />
              Ana Sayfa
            </MobileNavLink>
            <MobileNavLink to="/explore" onClick={() => setShowMobileMenu(false)}>
              <FiSearch size={18} />
              KeÅŸfet
            </MobileNavLink>
            {user && (
              <>
                <MobileNavLink to="/saved" onClick={() => setShowMobileMenu(false)}>
                  <FiBookmark size={18} />
                  Kaydedilenler
                </MobileNavLink>
                <MobileNavLink to={`/profile/${user.username}`} onClick={() => setShowMobileMenu(false)}>
                  <FiUser size={14} />
                  Profil
                </MobileNavLink>
                <MobileNavLink to="/settings" onClick={() => setShowMobileMenu(false)}>
                  <FiSettings size={18} />
                  Ayarlar
                </MobileNavLink>
                <MobileNavLink to="#" onClick={handleLogout}>
                  <FiLogOut size={18} />
                  Ã‡Ä±kÄ±ÅŸ Yap
                </MobileNavLink>
              </>
            )}
            {!user && (
              <>
                <MobileNavLink to="/login" onClick={() => setShowMobileMenu(false)}>
                  GiriÅŸ Yap
                </MobileNavLink>
                <MobileNavLink to="/register" onClick={() => setShowMobileMenu(false)}>
                  KayÄ±t Ol
                </MobileNavLink>
              </>
            )}
          </MobileMenu>
        )}
      </AnimatePresence>

    </Nav>
  );
};

export default Navbar;
