import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { FiHeart, FiMessageCircle, FiCalendar, FiTrendingUp } from 'react-icons/fi';

const SidebarContainer = styled.div`
  width: 100%;
  background: ${props => props.theme.surface};
  border-radius: 16px;
  padding: 24px;
  box-shadow: 0 4px 20px rgba(0, 0, 0, 0.1);
  border: 1px solid ${props => props.theme.border};
  max-height: calc(100vh - 120px);
  overflow-y: auto;
  position: relative;
  z-index: 10;

  @media (max-width: 1200px) {
    display: none;
  }
`;

const SidebarTitle = styled.h3`
  font-size: 1.25rem;
  font-weight: 700;
  color: ${props => props.theme.text};
  margin-bottom: 20px;
  display: flex;
  align-items: center;
  gap: 8px;
`;

const ArticleCard = styled(motion.div)`
  background: ${props => props.theme.cardBackground};
  border: 1px solid ${props => props.theme.border};
  border-radius: 12px;
  padding: 16px;
  margin-bottom: 16px;
  transition: all 0.3s ease;

  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 8px 25px rgba(0, 0, 0, 0.1);
    border-color: ${props => props.theme.primary};
  }
`;

const ArticleHeader = styled.div`
  display: flex;
  align-items: center;
  gap: 12px;
  margin-bottom: 12px;
`;

const AuthorAvatar = styled.div`
  width: 40px;
  height: 40px;
  border-radius: 50%;
  background: ${props => props.theme.gradient};
  display: flex;
  align-items: center;
  justify-content: center;
  color: white;
  font-weight: 600;
  font-size: 0.9rem;
`;

const AuthorInfo = styled.div`
  flex: 1;
`;

const AuthorName = styled.h4`
  font-size: 0.9rem;
  font-weight: 600;
  color: ${props => props.theme.text};
  margin: 0 0 2px 0;
`;

const ArticleDate = styled.span`
  font-size: 0.75rem;
  color: ${props => props.theme.textSecondary};
  display: flex;
  align-items: center;
  gap: 4px;
`;

const ArticleTitle = styled.h5`
  font-size: 1rem;
  font-weight: 600;
  color: ${props => props.theme.text};
  margin: 0 0 8px 0;
  line-height: 1.4;
`;

const ArticleExcerpt = styled.p`
  font-size: 0.85rem;
  color: ${props => props.theme.textSecondary};
  line-height: 1.5;
  margin: 0 0 12px 0;
  display: -webkit-box;
  -webkit-line-clamp: 3;
  -webkit-box-orient: vertical;
  overflow: hidden;
`;

const ArticleStats = styled.div`
  display: flex;
  align-items: center;
  gap: 16px;
  font-size: 0.75rem;
  color: ${props => props.theme.textSecondary};
`;

const StatItem = styled.div`
  display: flex;
  align-items: center;
  gap: 4px;
`;

const TrendingBadge = styled.div`
  background: linear-gradient(135deg, #FF6B35 0%, #F7931E 100%);
  color: white;
  padding: 4px 8px;
  border-radius: 6px;
  font-size: 0.7rem;
  font-weight: 600;
  display: flex;
  align-items: center;
  gap: 4px;
  margin-left: auto;
`;

const ViewAllButton = styled(motion.button)`
  width: 100%;
  padding: 12px;
  background: ${props => props.theme.gradient};
  color: white;
  border: none;
  border-radius: 8px;
  font-size: 0.9rem;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s ease;
  margin-top: 8px;

  &:hover {
    transform: translateY(-1px);
    box-shadow: 0 4px 15px rgba(0, 0, 0, 0.2);
  }
`;



const ArtistsSidebar = () => {
  // Mock data for artist articles
  const articles = [
    {
      id: 1,
      author: {
        name: "Zeynep Kaya",
        avatar: "ZK",
        isVerified: true
      },
      title: "Dijital Sanatta Yeni Dönem: AI ile Yaratıcılık",
      excerpt: "Yapay zeka teknolojilerinin sanat dünyasına etkisi ve yaratıcı süreçlerdeki rolü hakkında düşüncelerim...",
      date: "2 gün önce",
      likes: 124,
      comments: 23,
      isTrending: true
    },
    {
      id: 2,
      author: {
        name: "Mehmet Özkan",
        avatar: "MÖ",
        isVerified: true
      },
      title: "Minimalist Tasarımın Gücü",
      excerpt: "Sade ve etkili tasarım prensipleri ile nasıl daha güçlü görsel hikayeler anlatabiliriz?",
      date: "4 gün önce",
      likes: 89,
      comments: 15,
      isTrending: false
    },
    {
      id: 3,
      author: {
        name: "Ayşe Demir",
        avatar: "AD",
        isVerified: false
      },
      title: "Renk Teorisi ve Duygusal Tasarım",
      excerpt: "Renklerin psikolojik etkileri ve kullanıcı deneyimindeki önemi üzerine bir analiz...",
      date: "1 hafta önce",
      likes: 67,
      comments: 12,
      isTrending: false
    },
    {
      id: 4,
      author: {
        name: "Can Yılmaz",
        avatar: "CY",
        isVerified: true
      },
      title: "Sürdürülebilir Tasarım Yaklaşımları",
      excerpt: "Çevre dostu tasarım prensipleri ve gelecek nesillere bırakacağımız miras...",
      date: "1 hafta önce",
      likes: 156,
      comments: 31,
      isTrending: true
    }
  ];

  return (
    <SidebarContainer>
      <HeaderTitle>
        <FiTrendingUp size={18} />
        Sanatçı Köşe Yazıları
      </HeaderTitle>

      <DropdownButton>
        Son Yazılar
        <FiTrendingUp size={14} />
      </DropdownButton>
    </SidebarContainer>
  );
};

export default ArtistsSidebar;
