import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { FiMail, FiLock, FiEye, FiEyeOff, FiArrowRight, FiSun, FiMoon } from 'react-icons/fi';
import { useAuth } from '../contexts/AuthContext';
import { useTheme } from '../contexts/ThemeContext';

const Container = styled.div`
  min-height: 100vh;
  background: ${props => props.theme.background};
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 20px 200px 20px 20px;
  position: relative;
  overflow: hidden;
`;

const LeafContainer = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  pointer-events: none;
  z-index: 1;
`;

const Leaf = styled(motion.div)`
  position: absolute;
  width: ${props => props.size}px;
  height: ${props => props.size}px;
  background: ${props => props.color};
  border-radius: 0 100% 0 100%;
  opacity: 0.8;
  top: -10px;
  left: ${props => props.left}%;
`;

const LeftSection = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  padding: 40px;
  position: relative;
  z-index: 1;
  height: 100vh;
`;

const ArtworkContainer = styled.div`
  position: relative;
  width: 100%;
  max-width: 500px;
  height: 400px;
  display: flex;
  align-items: center;
  justify-content: center;
`;

const FloatingElement = styled(motion.div)`
  position: absolute;
  background: ${props => props.color};
  border-radius: ${props => props.radius};
  opacity: 0.8;
  filter: blur(${props => props.blur}px);
`;

const ArtworkTitle = styled.h2`
  font-size: 3rem;
  font-weight: 900;
  background: linear-gradient(45deg, #FF6B35, #F7931E, #FFD700, #FF8C42);
  background-size: 300% 300%;
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  margin-top: 60px;
  margin-bottom: 20px;
  text-align: center;
  animation: gradientShift 2s ease-in-out infinite, pulse 1.5s ease-in-out infinite;
  text-shadow: 0 0 50px rgba(255, 107, 53, 0.8), 0 0 100px rgba(255, 107, 53, 0.6);

  @keyframes gradientShift {
    0%, 100% { background-position: 0% 50%; }
    50% { background-position: 100% 50%; }
  }

  @keyframes pulse {
    0%, 100% { 
      transform: scale(1);
      filter: brightness(1);
    }
    50% { 
