const Login = () => {
  const navigate = useNavigate();
  const { login } = useAuth();
  const { theme, toggleTheme } = useTheme();
  const [formData, setFormData] = useState({
    email: '',
    password: '',
  });
  const [showPassword, setShowPassword] = useState(false);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
    setError('');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    if (!formData.email || !formData.password) {
      setError('Tüm alanları doldurun');
      setLoading(false);
      return;
    }

    const result = await login(formData.email, formData.password);

    if (result.success) {
      navigate('/');
    } else {
      setError(result.message || 'Giriş yapılırken hata oluştu');
    }

    setLoading(false);
  };

  return (
    <Container>
      <LeafContainer>
        {[...Array(15)].map((_, i) => (
          <Leaf
            key={i}
            size={Math.random() * 20 + 10}
            color={`hsl(${Math.random() * 40 + 20}, 80%, 60%)`}
            left={Math.random() * 100}
            initial={{ y: -50, rotate: 0 }}
            animate={{
              y: [0, window.innerHeight + 50],
              rotate: [0, 360, 720],
              x: [0, Math.random() * 100 - 50, Math.random() * 100 - 50]
            }}
            transition={{
              duration: Math.random() * 10 + 10,
              delay: Math.random() * 5,
              repeat: Infinity,
              ease: "linear"
            }}
          />
        ))}
      </LeafContainer>

      <LeftSection>
        <ArtworkTitle>3D Sanat Dünyası</ArtworkTitle>
        <ArtworkSubtitle>
          Yaratıcılığınızı dijital dünyada hayata geçirin. 
          Üç boyutlu sanat eserlerinizi dünyayla paylaşın.
        </ArtworkSubtitle>

        <ArtworkContainer>
          <FloatingElement
            color="linear-gradient(45deg, #FF6B35, #F7931E)"
            radius="50%"
            blur="0"
            width="120px"
            height="120px"
            top="20%"
            left="10%"
            animate={{
              y: [0, -20, 0],
              rotate: [0, 180, 360],
            }}
            transition={{
              duration: 8,
              repeat: Infinity,
              ease: "easeInOut"
            }}
          />

          <FloatingElement
            color="linear-gradient(45deg, #4F46E5, #7C3AED)"
            radius="20px"
            blur="2"
            width="80px"
            height="80px"
            top="60%"
            left="70%"
            animate={{
              y: [0, 20, 0],
              x: [0, 10, 0],
              rotate: [0, -90, 0],
            }}
            transition={{
              duration: 6,
              repeat: Infinity,
              ease: "easeInOut"
            }}
          />

          <FloatingElement
            color="linear-gradient(45deg, #10B981, #059669)"
            radius="30px"
            blur="1"
            width="100px"
            height="100px"
            top="40%"
            left="50%"
            animate={{
              y: [0, -15, 0],
              x: [0, -15, 0],
              rotate: [0, 270, 0],
            }}
            transition={{
              duration: 10,
              repeat: Infinity,
              ease: "easeInOut"
            }}
          />

          <FloatingElement
            color="linear-gradient(45deg, #EF4444, #DC2626)"
            radius="40px"
            blur="3"
            width="60px"
            height="60px"
            top="10%"
            left="80%"
            animate={{
              y: [0, 25, 0],
              rotate: [0, -180, 0],
            }}
            transition={{
              duration: 7,
              repeat: Infinity,
              ease: "easeInOut"
            }}
          />
        </ArtworkContainer>
      </LeftSection>

      <RightSection>
        <LoginCard
          initial={{ opacity: 0, y: 30 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
        >
        <Logo>
          <LogoIcon>F</LogoIcon>
          <LogoText>Feellink</LogoText>
          <LogoSubtext>Hesabınıza giriş yapın</LogoSubtext>
        </Logo>

        <Form onSubmit={handleSubmit}>
          {error && <ErrorMessage>{error}</ErrorMessage>}

          <FormGroup>
            <Label htmlFor="email">E-posta</Label>
            <InputContainer>
              <Input
                type="email"
                id="email"
                name="email"
                placeholder="ornek@email.com"
                value={formData.email}
                onChange={handleChange}
                required
              />
              <InputIcon>
                <FiMail />
              </InputIcon>
            </InputContainer>
          </FormGroup>

          <FormGroup>
            <Label htmlFor="password">Şifre</Label>
            <InputContainer>
              <Input
                type={showPassword ? 'text' : 'password'}
                id="password"
                name="password"
                placeholder="Şifrenizi girin"
                value={formData.password}
                onChange={handleChange}
                required
              />
              <InputIcon>
                <FiLock />
              </InputIcon>
              <PasswordToggle
                type="button"
                onClick={() => setShowPassword(!showPassword)}
              >
                {showPassword ? <FiEyeOff size={20} /> : <FiEye size={20} />}
              </PasswordToggle>
            </InputContainer>
          </FormGroup>

          <SubmitButton type="submit" disabled={loading}>
            {loading ? 'Giriş yapılıyor...' : 'Giriş Yap'}
            {!loading && <FiArrowRight size={18} />}
          </SubmitButton>
        </Form>

        <Divider>veya</Divider>

        <SignupLink>
          Hesabınız yok mu?{' '}
          <Link to="/register">Kayıt olun</Link>
        </SignupLink>
        </LoginCard>
      </RightSection>
    </Container>
  );
};

export default Login;
