const Navbar = () => {
  const { user, logout } = useAuth();
  const { isDark, toggleTheme } = useTheme();
  const navigate = useNavigate();
  const location = useLocation();
  const [showUserMenu, setShowUserMenu] = useState(false);
  const [showMobileMenu, setShowMobileMenu] = useState(false);

  const handleLogout = () => {
    logout();
    setShowUserMenu(false);
    navigate('/');
  };

  const isActive = (path) => {
    return location.pathname === path;
  };

  return (
    <Nav>
      <NavContainer>
        <Logo to="/">
          <LogoIcon>F</LogoIcon>
          Feellink
        </Logo>

        <NavLinks>
          <NavLink to="/" className={isActive('/') ? 'active' : ''}>
            <FiHome size={18} />
            Ana Sayfa
          </NavLink>
          <NavLink to="/explore" className={isActive('/explore') ? 'active' : ''}>
            <FiSearch size={18} />
            Keşfet
          </NavLink>
          {user && (
            <>
              <NavLink to="/saved" className={isActive('/saved') ? 'active' : ''}>
                <FiBookmark size={18} />
                Kaydedilenler
              </NavLink>
              <NavLink to={`/profile/${user.username}`} className={isActive(`/profile/${user.username}`) ? 'active' : ''}>
                <FiUser size={18} />
                Profil
              </NavLink>
            </>
          )}
        </NavLinks>

        <NavActions>
          <ThemeToggle onClick={toggleTheme}>
            {isDark ? <FiSun size={20} /> : <FiMoon size={20} />}
          </ThemeToggle>

          {user ? (
            <UserMenu>
              <UserButton onClick={() => setShowUserMenu(!showUserMenu)}>
                {user.avatar ? (
                  <UserAvatar src={user.avatar} alt={user.fullName} />
                ) : (
                  user.fullName.charAt(0).toUpperCase()
                )}
              </UserButton>

              <AnimatePresence>
                {showUserMenu && (
                  <Dropdown
                    initial={{ opacity: 0, y: -10 }}
                    animate={{ opacity: 1, y: 0 }}
                    exit={{ opacity: 0, y: -10 }}
                    transition={{ duration: 0.2 }}
                  >
                    <DropdownItem onClick={() => navigate(`/profile/${user.username}`)}>
                      <FiUser size={18} />
                      Profilim
                    </DropdownItem>
                    <DropdownItem onClick={() => navigate('/saved')}>
                      <FiBookmark size={18} />
                      Kaydedilenler
                    </DropdownItem>
                    <DropdownItem onClick={() => navigate('/settings')}>
                      <FiSettings size={18} />
                      Ayarlar
                    </DropdownItem>
                    <DropdownItem onClick={handleLogout}>
                      <FiLogOut size={18} />
                      Çıkış Yap
                    </DropdownItem>
                  </Dropdown>
                )}
              </AnimatePresence>
            </UserMenu>
          ) : (
            <AuthButtons>
              <AuthButton to="/login" className="secondary">
                Giriş Yap
              </AuthButton>
              <AuthButton to="/register" className="primary">
                Kayıt Ol
              </AuthButton>
            </AuthButtons>
          )}

          <MobileMenuButton onClick={() => setShowMobileMenu(!showMobileMenu)}>
            {showMobileMenu ? <FiX size={20} /> : <FiMenu size={20} />}
          </MobileMenuButton>
        </NavActions>
      </NavContainer>

      <AnimatePresence>
        {showMobileMenu && (
          <MobileMenu
            initial={{ opacity: 0, height: 0 }}
            animate={{ opacity: 1, height: 'auto' }}
            exit={{ opacity: 0, height: 0 }}
            transition={{ duration: 0.3 }}
          >
            <MobileNavLink to="/" onClick={() => setShowMobileMenu(false)}>
              <FiHome size={18} />
              Ana Sayfa
            </MobileNavLink>
            <MobileNavLink to="/explore" onClick={() => setShowMobileMenu(false)}>
              <FiSearch size={18} />
              Keşfet
            </MobileNavLink>
            {user && (
              <>
                <MobileNavLink to="/saved" onClick={() => setShowMobileMenu(false)}>
                  <FiBookmark size={18} />
                  Kaydedilenler
                </MobileNavLink>
                <MobileNavLink to={`/profile/${user.username}`} onClick={() => setShowMobileMenu(false)}>
                  <FiUser size={18} />
                  Profil
                </MobileNavLink>
                <MobileNavLink to="/settings" onClick={() => setShowMobileMenu(false)}>
                  <FiSettings size={18} />
                  Ayarlar
                </MobileNavLink>
                <MobileNavLink to="#" onClick={handleLogout}>
                  <FiLogOut size={18} />
                  Çıkış Yap
                </MobileNavLink>
              </>
            )}
            {!user && (
              <>
                <MobileNavLink to="/login" onClick={() => setShowMobileMenu(false)}>
                  Giriş Yap
                </MobileNavLink>
                <MobileNavLink to="/register" onClick={() => setShowMobileMenu(false)}>
                  Kayıt Ol
                </MobileNavLink>
              </>
            )}
          </MobileMenu>
        )}
      </AnimatePresence>
    </Nav>
  );
};

export default Navbar;
