  color: ${props => props.theme.textSecondary};
  font-size: 0.9rem;
`;

const Home = () => {
  const [searchQuery, setSearchQuery] = useState('');

  // Fetch featured works
  const { data: featuredWorks, isLoading: featuredLoading } = useQuery(
    'featured-works',
    async () => {
      const response = await axios.get('/api/works?featured=true&limit=10');
      return response.data.works;
    }
  );

  // Fetch categories
  const { data: categories, isLoading: categoriesLoading } = useQuery(
    'categories',
    async () => {
      const response = await axios.get('/api/categories');
      return response.data.categories;
    }
  );

  const handleSearch = (e) => {
    e.preventDefault();
    if (searchQuery.trim()) {
      window.location.href = `/explore?search=${encodeURIComponent(searchQuery)}`;
    }
  };

  return (
    <Container>
      <HeroSection>
        <HeroContent>
          <HeroTitle
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
          >
            Yaratıcılığınızı
            <br />
            Dünyayla Paylaşın
          </HeroTitle>

          <HeroSubtitle
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.2 }}
          >
            Behance benzeri platformumuzda eserlerinizi sergileyin, 
            ilham alın ve yaratıcı toplulukla bağlantı kurun.
          </HeroSubtitle>

          <SearchContainer
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.4 }}
          >
            <form onSubmit={handleSearch}>
              <SearchInput
                type="text"
                placeholder="Eserler, tasarımcılar veya kategoriler ara..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
              />
              <SearchIcon />
            </form>
          </SearchContainer>

          <StatsContainer
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.6 }}
          >
            <StatItem>
              <StatNumber>10K+</StatNumber>
              <StatLabel>Eser</StatLabel>
            </StatItem>
            <StatItem>
              <StatNumber>5K+</StatNumber>
              <StatLabel>Tasarımcı</StatLabel>
            </StatItem>
            <StatItem>
              <StatNumber>50K+</StatNumber>
              <StatLabel>Beğeni</StatLabel>
            </StatItem>
          </StatsContainer>
        </HeroContent>
      </HeroSection>

      <FeaturedSection>
        <SectionContainer>
          <SectionHeader>
            <SectionTitle>Öne Çıkan Eserler</SectionTitle>
            <SectionSubtitle>
              Topluluk tarafından en çok beğenilen ve ilham verici eserler
            </SectionSubtitle>
          </SectionHeader>

          {featuredLoading ? (
            <LoadingSpinner text="Eserler yükleniyor..." />
          ) : (
            <>
              <WorksGrid>
                {featuredWorks?.slice(0, 5).map((work, index) => (
                  <motion.div
                    key={work._id}
                    initial={{ opacity: 0, y: 30 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ duration: 0.6, delay: index * 0.1 }}
                  >
                    <WorkCard work={work} />
                  </motion.div>
                ))}
              </WorksGrid>

              <ViewAllButton
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
                onClick={() => window.location.href = '/explore'}
              >
                Tümünü Gör
                <FiArrowRight />
              </ViewAllButton>
            </>
          )}
        </SectionContainer>
      </FeaturedSection>

      <CategoriesSection>
        <SectionContainer>
          <SectionHeader>
            <SectionTitle>Kategoriler</SectionTitle>
            <SectionSubtitle>
              Farklı tasarım alanlarında eserlerinizi keşfedin
            </SectionSubtitle>
          </SectionHeader>

          {categoriesLoading ? (
            <LoadingSpinner text="Kategoriler yükleniyor..." />
          ) : (
            <CategoriesGrid>
              {categories?.slice(0, 6).map((category, index) => (
                <CategoryCard
                  key={category._id}
                  initial={{ opacity: 0, y: 30 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ duration: 0.6, delay: index * 0.1 }}
                  whileHover={{ scale: 1.02 }}
                  onClick={() => window.location.href = `/explore?category=${category._id}`}
                >
                  <CategoryIcon color={category.color}>
                    {category.icon || '🎨'}
                  </CategoryIcon>
                  <CategoryName>{category.name}</CategoryName>
                  <CategoryCount>Eser sayısı yükleniyor...</CategoryCount>
                </CategoryCard>
              ))}
            </CategoriesGrid>
          )}
        </SectionContainer>
      </CategoriesSection>
    </Container>
  );
};

export default Home;
