import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { FiMail, FiLock, FiUser, FiEye, FiEyeOff, FiArrowRight } from 'react-icons/fi';
import { useAuth } from '../contexts/AuthContext';

const Container = styled.div`
  min-height: 100vh;
  background: ${props => props.theme.background};
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 20px 120px 20px 20px;
  position: relative;
  overflow: hidden;
`;

const LeafContainer = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  pointer-events: none;
  z-index: 1;
`;

const Leaf = styled(motion.div)`
  position: absolute;
  width: ${props => props.size}px;
  height: ${props => props.size}px;
  background: ${props => props.color};
  border-radius: 0 100% 0 100%;
  opacity: 0.8;
  top: -10px;
  left: ${props => props.left}%;
`;

const LeftSection = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  padding: 40px;
  position: relative;
  z-index: 1;
`;

const ArtworkContainer = styled.div`
  position: relative;
  width: 100%;
  max-width: 500px;
  height: 400px;
  display: flex;
  align-items: center;
  justify-content: center;
`;

const FloatingElement = styled(motion.div)`
  position: absolute;
  background: ${props => props.color};
  border-radius: ${props => props.radius};
  opacity: 0.8;
  filter: blur(${props => props.blur}px);
`;

const ArtworkTitle = styled.h2`
  font-size: 2.5rem;
  font-weight: 800;
  color: ${props => props.theme.primary};
  margin-bottom: 16px;
  text-align: center;
`;

const ArtworkSubtitle = styled.p`
  font-size: 1.2rem;
  color: ${props => props.theme.textSecondary};
  text-align: center;
  max-width: 400px;
  line-height: 1.6;
`;

const RightSection = styled.div`
  flex: 0 0 400px;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 20px;
`;

const RegisterCard = styled(motion.div)`
  background: ${props => props.theme.surface};
  border: 1px solid ${props => props.theme.border};
  border-radius: 24px;
  padding: 48px;
  width: 100%;
  max-width: 400px;
  box-shadow: 0 20px 60px ${props => props.theme.shadow};
`;

const Logo = styled.div`
  text-align: center;
  margin-bottom: 40px;
`;

const LogoIcon = styled.div`
  width: 60px;
  height: 60px;
  background: ${props => props.theme.gradient};
  border-radius: 16px;
  display: flex;
  align-items: center;
  justify-content: center;
  color: white;
  font-size: 2rem;
  font-weight: bold;
  margin: 0 auto 16px;
`;

const LogoText = styled.h1`
  font-size: 2rem;
  font-weight: 800;
  color: ${props => props.theme.primary};
  margin-bottom: 8px;
`;

const LogoSubtext = styled.p`
  color: ${props => props.theme.textSecondary};
  font-size: 1rem;
`;

const Form = styled.form`
  display: flex;
  flex-direction: column;
  gap: 24px;
`;

const FormGroup = styled.div`
  position: relative;
`;

const Label = styled.label`
  display: block;
  font-weight: 600;
  color: ${props => props.theme.text};
  margin-bottom: 8px;
  font-size: 0.9rem;
`;

const InputContainer = styled.div`
  position: relative;
`;

const Input = styled.input`
  width: 100%;
  padding: 16px 20px 16px 52px;
  border: 2px solid ${props => props.theme.border};
  border-radius: 12px;
  background: ${props => props.theme.background};
  color: ${props => props.theme.text};
  font-size: 16px;
  transition: all 0.3s ease;

  &:focus {
    outline: none;
    border-color: ${props => props.theme.primary};
    box-shadow: 0 0 0 3px ${props => props.theme.primaryLight};
  }

  &::placeholder {
    color: ${props => props.theme.textMuted};
  }
`;

const InputIcon = styled.div`
  position: absolute;
  left: 18px;
  top: 50%;
  transform: translateY(-50%);
  color: ${props => props.theme.textMuted};
  font-size: 20px;
`;

const PasswordToggle = styled.button`
  position: absolute;
  right: 18px;
  top: 50%;
  transform: translateY(-50%);
  background: none;
  border: none;
  color: ${props => props.theme.textMuted};
  cursor: pointer;
  padding: 4px;
  transition: color 0.3s ease;

  &:hover {
    color: ${props => props.theme.primary};
  }
`;

const SubmitButton = styled.button`
  width: 100%;
  padding: 16px;
  background: ${props => props.theme.primary};
  color: white;
  border: none;
  border-radius: 12px;
  font-size: 16px;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s ease;
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 8px;

  &:hover {
    background: ${props => props.theme.primaryHover};
    transform: translateY(-2px);
    box-shadow: 0 10px 30px rgba(255, 107, 53, 0.3);
  }

  &:disabled {
    opacity: 0.6;
    cursor: not-allowed;
    transform: none;
  }
`;

const Divider = styled.div`
  display: flex;
  align-items: center;
  margin: 32px 0;
  color: ${props => props.theme.textMuted};
  font-size: 0.9rem;

  &::before,
  &::after {
    content: '';
    flex: 1;
    height: 1px;
    background: ${props => props.theme.border};
  }

  &::before {
    margin-right: 16px;
  }

  &::after {
    margin-left: 16px;
  }
`;

const LoginLink = styled.div`
  text-align: center;
  color: ${props => props.theme.textSecondary};
  font-size: 0.9rem;

  a {
    color: ${props => props.theme.primary};
    text-decoration: none;
    font-weight: 600;
    transition: color 0.3s ease;

    &:hover {
      color: ${props => props.theme.primaryHover};
    }
  }
`;

const ErrorMessage = styled.div`
  background: ${props => props.theme.error}20;
  border: 1px solid ${props => props.theme.error};
  color: ${props => props.theme.error};
  padding: 12px 16px;
  border-radius: 8px;
  font-size: 0.9rem;
  margin-bottom: 16px;
`;

const PasswordStrength = styled.div`
  margin-top: 8px;
  font-size: 0.8rem;
`;

const StrengthBar = styled.div`
  display: flex;
  gap: 4px;
  margin-top: 4px;
`;

const StrengthItem = styled.div`
  flex: 1;
  height: 4px;
  background: ${props => props.active ? props.color : props.theme.border};
  border-radius: 2px;
  transition: background 0.3s ease;
`;

const Register = () => {
  const navigate = useNavigate();
  const { register } = useAuth();
  const [formData, setFormData] = useState({
    fullName: '',
    username: '',
    email: '',
    password: '',
    confirmPassword: '',
  });
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
    setError('');
  };

  const validatePassword = (password) => {
    return {
      length: password.length >= 6,
      uppercase: /[A-Z]/.test(password),
      lowercase: /[a-z]/.test(password),
      number: /\d/.test(password),
    };
  };

  const passwordValidation = validatePassword(formData.password);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    // Validation
    if (!formData.fullName || !formData.username || !formData.email || !formData.password) {
      setError('Tüm alanları doldurun');
      setLoading(false);
      return;
    }

    if (formData.password !== formData.confirmPassword) {
      setError('Şifreler eşleşmiyor');
      setLoading(false);
      return;
    }

    if (formData.password.length < 6) {
      setError('Şifre en az 6 karakter olmalı');
      setLoading(false);
      return;
    }

    if (formData.username.length < 3) {
      setError('Kullanıcı adı en az 3 karakter olmalı');
      setLoading(false);
      return;
    }

    const result = await register(formData);

    if (result.success) {
      navigate('/');
    } else {
      setError(result.message || 'Kayıt olurken hata oluştu');
    }

    setLoading(false);
  };

  return (
    <Container>
      <LeafContainer>
        {[...Array(15)].map((_, i) => (
          <Leaf
            key={i}
            size={Math.random() * 20 + 10}
            color={`hsl(${Math.random() * 40 + 20}, 80%, 60%)`}
            left={Math.random() * 100}
            initial={{ y: -50, rotate: 0 }}
            animate={{
              y: [0, window.innerHeight + 50],
              rotate: [0, 360, 720],
              x: [0, Math.random() * 100 - 50, Math.random() * 100 - 50]
            }}
            transition={{
              duration: Math.random() * 10 + 10,
              delay: Math.random() * 5,
              repeat: Infinity,
              ease: "linear"
            }}
          />
        ))}
      </LeafContainer>

      <LeftSection>
        <ArtworkTitle>Yaratıcılığınızı Keşfedin</ArtworkTitle>
        <ArtworkSubtitle>
          Dijital sanat dünyasına adım atın. 
          Üç boyutlu eserlerinizi dünyayla paylaşın ve ilham verin.
        </ArtworkSubtitle>

        <ArtworkContainer>
          <FloatingElement
            color="linear-gradient(45deg, #8B5CF6, #A855F7)"
            radius="50%"
            blur="0"
            width="100px"
            height="100px"
            top="15%"
            left="20%"
            animate={{
              y: [0, -25, 0],
              rotate: [0, 360, 0],
            }}
            transition={{
              duration: 9,
              repeat: Infinity,
              ease: "easeInOut"
            }}
          />

          <FloatingElement
            color="linear-gradient(45deg, #06B6D4, #0891B2)"
            radius="25px"
            blur="2"
            width="90px"
            height="90px"
            top="65%"
            left="15%"
            animate={{
              y: [0, 20, 0],
              x: [0, -10, 0],
              rotate: [0, -180, 0],
            }}
            transition={{
              duration: 7,
              repeat: Infinity,
              ease: "easeInOut"
            }}
          />

          <FloatingElement
            color="linear-gradient(45deg, #F59E0B, #D97706)"
            radius="35px"
            blur="1"
            width="110px"
            height="110px"
            top="35%"
            left="60%"
            animate={{
              y: [0, -20, 0],
              x: [0, 15, 0],
              rotate: [0, 180, 0],
            }}
            transition={{
              duration: 11,
              repeat: Infinity,
              ease: "easeInOut"
            }}
          />

          <FloatingElement
            color="linear-gradient(45deg, #EC4899, #DB2777)"
            radius="40px"
            blur="3"
            width="70px"
            height="70px"
            top="5%"
            left="75%"
            animate={{
              y: [0, 30, 0],
              rotate: [0, -270, 0],
            }}
            transition={{
              duration: 8,
              repeat: Infinity,
              ease: "easeInOut"
            }}
          />
        </ArtworkContainer>
      </LeftSection>

      <RightSection>
        <RegisterCard
          initial={{ opacity: 0, y: 30 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
        >
          <Logo>
            <LogoIcon>F</LogoIcon>
            <LogoText>Feellink</LogoText>
            <LogoSubtext>Hesap oluşturun</LogoSubtext>
          </Logo>

          <Form onSubmit={handleSubmit}>
            {error && <ErrorMessage>{error}</ErrorMessage>}

            <FormGroup>
              <Label htmlFor="fullName">Ad Soyad</Label>
              <InputContainer>
                <Input
                  type="text"
                  id="fullName"
                  name="fullName"
                  placeholder="Adınız ve soyadınız"
                  value={formData.fullName}
                  onChange={handleChange}
                  required
                />
                <InputIcon>
                  <FiUser />
                </InputIcon>
              </InputContainer>
            </FormGroup>

            <FormGroup>
              <Label htmlFor="username">Kullanıcı Adı</Label>
              <InputContainer>
                <Input
                  type="text"
                  id="username"
                  name="username"
                  placeholder="kullaniciadi"
                  value={formData.username}
                  onChange={handleChange}
                  required
                />
                <InputIcon>
                  <FiUser />
                </InputIcon>
              </InputContainer>
            </FormGroup>

            <FormGroup>
              <Label htmlFor="email">E-posta</Label>
              <InputContainer>
                <Input
                  type="email"
                  id="email"
                  name="email"
                  placeholder="ornek@email.com"
                  value={formData.email}
                  onChange={handleChange}
                  required
                />
                <InputIcon>
                  <FiMail />
                </InputIcon>
              </InputContainer>
            </FormGroup>

            <FormGroup>
              <Label htmlFor="password">Şifre</Label>
              <InputContainer>
                <Input
                  type={showPassword ? 'text' : 'password'}
                  id="password"
                  name="password"
                  placeholder="Güçlü bir şifre oluşturun"
                  value={formData.password}
                  onChange={handleChange}
                  required
                />
                <InputIcon>
                  <FiLock />
                </InputIcon>
                <PasswordToggle
                  type="button"
                  onClick={() => setShowPassword(!showPassword)}
                >
                  {showPassword ? <FiEyeOff size={20} /> : <FiEye size={20} />}
                </PasswordToggle>
              </InputContainer>

              {formData.password && (
                <PasswordStrength>
                  <div>Şifre gücü:</div>
                  <StrengthBar>
                    <StrengthItem active={passwordValidation.length} color="#ef4444" />
                    <StrengthItem active={passwordValidation.lowercase} color="#f59e0b" />
                    <StrengthItem active={passwordValidation.uppercase} color="#10b981" />
                    <StrengthItem active={passwordValidation.number} color="#3b82f6" />
                  </StrengthBar>
                </PasswordStrength>
              )}
            </FormGroup>

            <FormGroup>
              <Label htmlFor="confirmPassword">Şifre Tekrar</Label>
              <InputContainer>
                <Input
                  type={showConfirmPassword ? 'text' : 'password'}
                  id="confirmPassword"
                  name="confirmPassword"
                  placeholder="Şifrenizi tekrar girin"
                  value={formData.confirmPassword}
                  onChange={handleChange}
                  required
                />
                <InputIcon>
                  <FiLock />
                </InputIcon>
                <PasswordToggle
                  type="button"
                  onClick={() => setShowConfirmPassword(!showConfirmPassword)}
                >
                  {showConfirmPassword ? <FiEyeOff size={20} /> : <FiEye size={20} />}
                </PasswordToggle>
              </InputContainer>
            </FormGroup>

            <SubmitButton type="submit" disabled={loading}>
              {loading ? 'Hesap oluşturuluyor...' : 'Hesap Oluştur'}
              {!loading && <FiArrowRight size={18} />}
            </SubmitButton>
          </Form>

          <Divider>veya</Divider>

          <LoginLink>
            Zaten hesabınız var mı?{' '}
            <Link to="/login">Giriş yapın</Link>
          </LoginLink>
        </RegisterCard>
      </RightSection>
    </Container>
  );
};

export default Register;
